/**
 * This class creates the circle atom.
 */
export default class Circle extends Atom {
  /**
   * The constructor function.
   * @param {object} values An array of values passed in which will be assigned to the class as this.x
   */
  constructor(values) {
    super(values);

    /**
     * This atom's name
     * @type {string}
     */
    this.name = "Circle";
    /**
     * This atom's type
     * @type {string}
     */
    this.atomType = "Circle";
    /**
     * A description of this atom
     * @type {string}
     */
    this.description = "Creates a new circle.";

    this.addIO("input", "diameter", this, "number", 10.0);
    this.addIO("output", "geometry", this, "geometry", "");

    this.setValues(values);
  }

  /**
   * Starts propagation from this atom if it is not waiting for anything up stream.
   */
  beginPropagation(force = false) {
    //Triggers inputs with nothing connected to begin propagation
    this.inputs.forEach((input) => {
      input.beginPropagation();
    });
  }

  /**
   * Draw the circle atom & icon.
   */
  draw() {
    super.draw(); //Super call to draw the rest

    GlobalVariables.c.beginPath();
    GlobalVariables.c.fillStyle = "#949294";
    GlobalVariables.c.arc(
      GlobalVariables.widthToPixels(this.x),
      GlobalVariables.heightToPixels(this.y),
      GlobalVariables.widthToPixels(this.radius / 2),
      0,
      Math.PI * 2,
      false
    );
    //GlobalVariables.c.fill()
    GlobalVariables.c.stroke();
    GlobalVariables.c.closePath();
  }

  /**
   * Update the value of the circle in worker.
   */
  updateValue() {
    super.updateValue();
    var diameter = this.findIOValue("diameter");
    GlobalVariables.cad
      .circle(this.uniqueID, diameter)
      .then(() => {
        this.basicThreadValueProcessing();
      })
      .catch(this.alertingErrorHandler());
  }
}

import Atom from "../prototypes/atom.js";

import GlobalVariables from "../js/globalvariables.js";
import { button } from "leva";

/**
 * The Code molecule type adds support for executing arbitrary jsxcad code.
 */
export default class Code extends Atom {
  /**
   * The constructor function.
   * @param {object} values An array of values passed in which will be assigned to the class as this.x
   */
  constructor(values) {
    super(values);

    /**
     * This atom's name
     * @type {string}
     */
    this.name = "Code";
    /**
     * This atom's name
     * @type {string}
     */
    this.atomType = "Code";
    /**
     * A description of this atom
     * @type {string}
     */
    this.description = "Defines a Replicad code block.";
    /**
     * The code contained within the atom stored as a string.
     * @type {string}
     */
    this.code =
      "\
//Inputs:[input1, height];\n\
\n\
let shape = drawRectangle(5,7)\n\
\n\
const newPlane = new Plane().pivot(0, 'Y');\n\
\n\
 return { geometry: [shape.sketchOnPlane(newPlane).extrude(7)], tags: [],\n\
      color: '#A3CE5B',\n\
      plane: newPlane}\n\
\n\
\n\
    /**\n\
    To Use the Code Atom, enter your inputs to the input list a.e Inputs:[shape, height]\n\
    If your input is connected to another atom with a replicad geometry you can access its geometry by looking up its ID in your library. a.e library[Input1].geometry[0] \n\
    Use any replicad available methods to modify your geometry. Learn more about all of the available methods at \n\
    https://replicad.xyz/docs/introapp/UserGuide.html \n\
    Return a replicad object that includes geometry, color, tags and plane. \n\
\n\
\n\
    Example Code Atom:\n\
\n\
      Inputs:[shape, x];\n\
\n\
      let finalShape = library[shape].geometry[0].clone.translate[x,0,0]\n\
\n\
      return {geometry: finalShape, color: library[shape].color, plane: library[shape].plane, tags: library[shape].tags }\n\
\n\
      - See more examples at _______ \n\
\n\
\n\
    */\n\
";

    this.addIO("output", "geometry", this, "geometry", "");

    this.setValues(values);

    this.parseInputs(false);
  }

  /**
   * Draw the code atom which has a code icon.
   */
  draw() {
    super.draw(); //Super call to draw the rest

    GlobalVariables.c.beginPath();
    GlobalVariables.c.fillStyle = "#949294";
    GlobalVariables.c.font = `${GlobalVariables.widthToPixels(
      this.radius
    )}px Work Sans Bold`;
    GlobalVariables.c.fillText(
      "</>",
      GlobalVariables.widthToPixels(this.x - this.radius / 1.5),
      GlobalVariables.heightToPixels(this.y + this.radius * 1.5)
    );
  }

  /**
   * Begin propagation from this code atom if it has no inputs or if none of the inputs are connected.
   */
  beginPropagation() {
    //If there are no inputs
    if (this.inputs.length == 0) {
      this.updateValue();
    }

    //If none of the inputs are connected
    var connectedInput = false;
    this.inputs.forEach((input) => {
      if (input.connectors.length > 0) {
        connectedInput = true;
      }
    });
    if (!connectedInput) {
      this.updateValue();
    }
  }

  createLevaInputs() {
    let inputParams = {};
    /** Runs through active atom inputs and adds IO parameters to default param*/
    if (this.inputs) {
      this.inputs.map((input) => {
        const checkConnector = () => {
          return input.connectors.length > 0;
        };

        inputParams[this.uniqueID + input.name] = {
          value: input.value,
          label: input.name,
          disabled: checkConnector(),
          onChange: (value) => {
            if (input.value !== value) {
              input.setValue(value);
              //this.sendToRender();
            }
          },
        };
      });
      inputParams["Edit Code"] = button(() => this.editCode());
      inputParams["Save Code"] = button(() => this.saveCode());
      inputParams["Close Editor"] = button(() => this.closeCode());
      return inputParams;
    }
  }

  /**
   * Called when code editor save button is clicked. Updates the code and value of the atom.
   */
  updateCode(code) {
    this.code = code;
    this.updateValue();
    this.sendToRender();
  }

  /**
   * Grab the code as a text string and execute it.
   */
  updateValue(value) {
    super.updateValue();
    //Parse the inputs
    this.parseInputs();

    if (this.inputs.every((x) => x.ready)) {
      var inputValues = [];
      this.inputs.forEach((io) => {
        if (io.connectors.length > 0 && io.type == "input") {
          inputValues.push(io.getValue());
        }
      });
      var argumentsArray = {};
      this.inputs.forEach((input) => {
        argumentsArray[input.name] = input.value;
      });

      GlobalVariables.cad
        .code(this.uniqueID, this.code, argumentsArray)
        .then((result) => {

          if(result === true){ //Code atom returned geometry
            this.basicThreadValueProcessing();
          }
          else{ //Code atom returned a number
            this.customThreadValueProcessing(result);
          }
        })
        .catch(this.alertingErrorHandler());
    }
  }

  /**
   * Override the standard basic thread processing function to allow passing of numbers or geometry depending on what we have
  */
  customThreadValueProcessing(returnedNumber) {
    this.decreaseToProcessCountByOne();
    this.clearAlert();
    if (this.output) {
      this.value = returnedNumber;
      this.output.setValue(returnedNumber);
      this.output.ready = true;
    }
    this.processing = false;
  }

  /**
   * This function reads the string of inputs the user specifies and adds them to the atom.
   */
  parseInputs(ready = true) {
    //Parse this.code for the line "\nmain(input1, input2....) and add those as inputs if needed
    var variables = /Inputs:\[\s*([^)]+?)\s*\]/.exec(this.code);

    if (variables) {
      if (variables[1]) {
        variables = variables[1].split(/\s*,\s*/);
      }
      let variableNames = [];
      //Add any inputs which are needed
      for (var variable in variables) {
        variables[variable] = variables[variable].split(/\s*=\s*/);
        let variableName = variables[variable][0];
        variableNames.push(variableName);
        let defaultVal = variables[variable][1] ? variables[variable][1] : 10;

        if (!this.inputs.some((input) => input.Name === variableName)) {
          this.addIO(
            "input",
            variableName,
            this,
            "geometry",
            defaultVal,
            ready
          );
        }
      }

      //Remove any inputs which are not needed
      for (var input in this.inputs) {
        if (!variableNames.includes(this.inputs[input].name)) {
          this.removeIO("input", this.inputs[input].name, this);
        }
      }
    }
  }

  /**
   * Edit the atom's code when it is double clicked
   * @param {number} x - The X coordinate of the click
   * @param {number} y - The Y coordinate of the click
   */
  doubleClick(x, y) {
    //returns true if something was done with the click
    let xInPixels = GlobalVariables.widthToPixels(this.x);
    let yInPixels = GlobalVariables.heightToPixels(this.y);
    var clickProcessed = false;

    var distFromClick = GlobalVariables.distBetweenPoints(
      x,
      xInPixels,
      y,
      yInPixels
    );

    if (distFromClick < this.radius) {
      this.editCode();
      clickProcessed = true;
    }

    return clickProcessed;
  }

  /**
   * Called to trigger editing the code atom
   */
  editCode() {
    const codeWindow = document.getElementById("code-window");
    codeWindow.classList.remove("code-off");
  }

  /**
   * Called to trigger editing the code atom
   */
  saveCode() {
    const saveCodeButton = document.getElementById("save-code-button");
    saveCodeButton.click();
  }

  /**
   * Called to trigger editing the code atom
   */
  closeCode() {
    const closeCodeButton = document.getElementById("close-code-button");
    closeCodeButton.click();
  }

  /**
   * Save the input code to be loaded next time
   */
  serialize(values) {
    //Save the readme text to the serial stream
    var valuesObj = super.serialize(values);
    valuesObj.codeVersion = 1;
    valuesObj.code = this.code;

    return valuesObj;
  }
}

import Atom from "../prototypes/atom";

import GlobalVariables from "../js/globalvariables.js";

/**
 * This class creates the color atom which can be used to give a part a color.
 */
export default class Color extends Atom {
  /**
   * The constructor function.
   * @param {object} values An array of values passed in which will be assigned to the class as this.x
   */
  constructor(values) {
    super(values);

    /**
     * This atom's name
     * @type {string}
     */
    this.name = "Color";
    /**
     * This atom's type
     * @type {string}
     */
    this.atomType = "Color";
    /**
     * A description of this atom
     * @type {string}
     */
    this.description = "Changes the color of the shape.";

    /**
     * The index of the currently selected color option.
     * @type {number}
     */
    this.selectedColorIndex = 0;

    /**
     * The color options to choose from
     * @type {array}
     */
    this.colorOptions = {
      Red: "#FF9065",
      Orange: "#FFB458",
      Yellow: "#FFD600",
      Olive: "#C7DF66",
      Teal: "#71D1C2",
      "Light Blue": "#75DBF2",
      Green: "#A3CE5B",
      "Lavender ": "#CCABED",
      Brown: "#CFAB7C",
      Pink: "#FFB09D",
      Sand: "#E2C66C",
      Clay: "#C4D3AC",
      Blue: "#91C8D5",
      "Light Green": "#96E1BB",
      Purple: "#ACAFDD",
      "Light Purple": "#DFB1E8",
      Tan: "#F5D3B6",
      "Mauve ": "#DBADA9",
      Grey: "#BABABA",
      Black: "#3C3C3C",
      White: "#FFFCF7",
      "Keep Out": "#D9544D",
    };

    this.addIO("input", "geometry", this, "geometry", null, false, true);
    this.addIO("output", "geometry", this, "geometry", null);

    this.selectedValueColor;

    this.setValues(values);
  }

  /**
   * Draw the circle atom & icon.
   */
  draw() {
    super.draw(); //Super call to draw the rest

    GlobalVariables.c.beginPath();
    GlobalVariables.c.fillStyle = Object.values(this.colorOptions)[
      this.selectedColorIndex
    ];

    GlobalVariables.c.arc(
      GlobalVariables.widthToPixels(this.x),
      GlobalVariables.heightToPixels(this.y),
      GlobalVariables.widthToPixels(this.radius / 1.5),
      0,
      Math.PI * 2,
      false
    );
    GlobalVariables.c.fill();
    GlobalVariables.c.closePath();
  }

  /**
   * Applies a color tag to the object in a worker thread.
   */
  updateValue() {
    super.updateValue();

    if (this.inputs.every((x) => x.ready)) {
      this.processing = true;
      var inputID = this.findIOValue("geometry");
      var color = Object.values(this.colorOptions)[this.selectedColorIndex];
      this.selectedValueColor = Object.keys(this.colorOptions)[
        this.selectedColorIndex
      ];
      GlobalVariables.cad
        .color(this.uniqueID, inputID, color)
        .then(() => {
          this.basicThreadValueProcessing();
        })
        .catch(this.alertingErrorHandler());
    }
  }

  /**
   * Updates the value of the selected color and then the value.
   */
  changeColor(index) {
    this.selectedColorIndex = index;
    this.updateValue();
  }

  /**
   * Create Leva Menu Inputs - returns to ParameterEditor
   */
  createLevaInputs() {
    let inputParams = {};
    /** Runs through active atom inputs and adds IO parameters to default param*/
    if (this.inputs) {
      this.inputs.map((input) => {
        const checkConnector = () => {
          return input.connectors.length > 0;
        };

        inputParams[this.uniqueID + "color"] = {
          value: Object.keys(this.colorOptions)[this.selectedColorIndex],
          label: "Color",
          options: Object.keys(this.colorOptions),
          onChange: (value) => {
            this.changeColor(Object.keys(this.colorOptions).indexOf(value));
            this.sendToRender();
          },
        };

        /* Makes inputs for Io's other than geometry */
        if (input.valueType !== "geometry") {
          inputParams[this.uniqueID + input.name] = {
            value: input.value,
            label: input.name,
            disabled: checkConnector(),
            onChange: (value) => {
              if (input.value !== value) {
                input.setValue(value);
                //this.sendToRender();
              }
            },
          };
        }
      });
      return inputParams;
    }
  }

  /**
   * Add the color choice to the object which is saved for this molecule
   */
  serialize(offset = { x: 0, y: 0 }) {
    var superSerialObject = super.serialize(offset);

    //Write the current color selection to the serialized object
    superSerialObject.selectedColorIndex = this.selectedColorIndex;

    return superSerialObject;
  }
}

import Atom from "../prototypes/atom";
import GlobalVariables from "../js/globalvariables.js";

/**
 * This class creates the constant atom instance which can be used to define a numerical constant.
 */
export default class Constant extends Atom {
  /**
   * Creates a new constant atom.
   * @param {object} values - An object of values. Each of these values will be applied to the resulting atom.
   */
  constructor(values) {
    super(values);

    /**
     * This atom's type
     * @type {string}
     */
    this.type = "constant";
    /**
     * This atom's name
     * @type {string}
     */
    this.name = "Constant";
    /**
     * This atom's type
     * @type {string}
     */
    this.atomType = "Constant";
    /**
     * A description of this atom
     * @type {string}
     */
    this.description = "Defines a mathematical constant.";
    /**
     * This atom's height as drawn on the screen
     */
    this.height = 16;
    /**
     * A flag to indicate if this constant should be evolved by genetic algorithms
     * @type {boolean}
     */
    this.evolve = false;
    /**
     * Minimum value to be used when evolving constant
     * @type {float}
     */
    this.min = 0;
    /**
     * Maximum value to be used when evolving constant
     * @type {float}
     */
    this.max = 20;

    /**
     * The default value for the constant
     * @type {float}
     */
    this.value = 10.0;

    this.setValues(values);

    this.addIO("output", "number", this, "number", 10.0);

    this.decreaseToProcessCountByOne(); //Since there is nothing upstream this needs to be removed from the list here
  }

  /**
   * Draw the Bill of material atom which has a BOM icon.
   */
  draw() {
    super.draw("rect");
    let pixelsX = GlobalVariables.widthToPixels(this.x);
    let pixelsY = GlobalVariables.heightToPixels(this.y);
    let pixelsRadius = GlobalVariables.widthToPixels(this.radius);
    /**
     * Relates height to radius
     * @type {number}
     */
    this.height = pixelsRadius;

    GlobalVariables.c.beginPath();
    GlobalVariables.c.fillStyle = "#484848";
    GlobalVariables.c.font = `${pixelsRadius}px Work Sans Bold`;
    const text = String.fromCharCode(0x039b);
    const textHeight = pixelsRadius / 1.5;
    const textWidth = GlobalVariables.c.measureText(text).width;
    const textX = pixelsX - textWidth / 2;
    const textY = pixelsY + this.height / 2 - textHeight / 2;
    GlobalVariables.c.fillText(text, textX, textY);

    GlobalVariables.c.fill();
    GlobalVariables.c.closePath();
  }
  /**
   * Create Leva Menu Input - returns to ParameterEditor
   */
  createLevaInputs() {
    // Create the Leva input for the constant name
    let outputParams = {};
    outputParams["constant number"] = {
      value: this.name,
      label: "Constant Name",
      disabled: false,
      onChange: (value) => {
        this.name = value;
      },
    };
    // Create the Leva input for the constant value
    outputParams[this.uniqueID + this.name] = {
      value: this.value,
      label: this.name,
      disabled: false,
      onChange: (value) => {
        if (this.value !== value) {
          this.output.setValue(value);
          this.updateValue();
        }
      },
    };
    return outputParams;
  }
  /**
   * Set's the output value for constant
   */
  updateValue() {
    this.value = this.output.getValue();
    this.output.ready = true;
    this.processing = false;
  }
  /**
   * Send the value of this atom to the 3D display. Used to display the number
   */
  sendToRender() {
    //Send code to jotcad to render
    GlobalVariables.writeToDisplay(this.uniqueID);
  }
}

import Atom from "../prototypes/atom.js";
