    super.updateValue();

    if (this.inputs.every((x) => x.ready)) {
      this.processing = true;
      const input1ID = this.findIOValue("geometry1");
      const input2ID = this.findIOValue("geometry2");

      GlobalVariables.cad
        .difference(this.uniqueID, input1ID, input2ID)
        .then(() => {
          this.basicThreadValueProcessing();
        })
        .catch(this.alertingErrorHandler());
    }
  }
}

import Atom from "../prototypes/atom";
import GlobalVariables from "../js/globalvariables.js";
import { button } from "leva";
import { or } from "mathjs";

/**
 * This class creates the Equation atom.
 */
export default class Equation extends Atom {
  /**
   * The constructor function.
   * @param {object} values An array of values passed in which will be assigned to the class as this.x
   */
  constructor(values) {
    super(values);

    this.addIO("output", "result", this, "number", 0);

    /**
     * This atom's name
     * @type {string}
     */
    this.name = "Equation";

    /**
     * This atom's type
     * @type {string}
     */
    this.atomType = "Equation";

    /**
     * Evaluate the equation adding and removing inputs as needed
     */
    this.value = 0;
    /**
     * A description of this atom
     * @type {string}
     */
    this.description =
      "Defines a mathematical equation. Edit the output field to add or remove inputs.";

    /**
     * This atom's height as drawn on the screen
     */
    this.height;
    /**
     * The index number of the currently selected option
     * @type {number}
     */
    this.currentEquation = "x + y";

    this.setValues(values);
    this.addAndRemoveInputs();
    this.setValues(values); //Set values again to load input values which were saved
  }

  /**
   * Draw the Bill of material atom which has a BOM icon.
   */
  draw() {
    super.draw("rect");

    let pixelsX = GlobalVariables.widthToPixels(this.x);
    let pixelsY = GlobalVariables.heightToPixels(this.y);
    let pixelsRadius = GlobalVariables.widthToPixels(this.radius);
    /**
     * Relates height to radius
     * @type {number}
     */
    this.height = pixelsRadius;

    GlobalVariables.c.beginPath();
    GlobalVariables.c.fillStyle = "#484848";
    GlobalVariables.c.font = `${pixelsRadius / 1.3}px Work Sans Bold`;

    const text = "\u221A" + "(+)";
    const textHeight = pixelsRadius / 1.5;
    const textWidth = GlobalVariables.c.measureText(text).width;
    const textX = pixelsX - textWidth / 2;
    const textY = pixelsY + this.height / 2 - textHeight / 2;
    GlobalVariables.c.fillText(text, textX, textY);

    GlobalVariables.c.fill();
    GlobalVariables.c.closePath();
  }

  /**
   * Add and remove inputs as needed from the atom
   */
  addAndRemoveInputs() {
    //Find all the letters in this equation
    var re = /[a-zA-Z]/g;
    const variables = this.currentEquation.match(re);

    //Remove any inputs which are not needed
    const deleteExtraInputs = () => {
      this.inputs.forEach((input) => {
        if (!variables.includes(input.name)) {
          this.removeIO("input", input.name, this);
          deleteExtraInputs(); //This needs to be called recursively to make sure all the inputs are deleted
        }
      });
    };
    deleteExtraInputs();

    //Add any inputs which are needed
    for (var variable in variables) {
      if (!this.inputs.some((input) => input.Name === variables[variable])) {
        this.addIO("input", variables[variable], this, "number", 1);
      }
    }
  }

  /**
   * Evaluate the equation
   */
  evaluateEquation() {
    //Substitute numbers into the string
    var substitutedEquation = this.currentEquation;
    this.name = this.currentEquation;

    //Find all the letters in this equation
    var re = /[a-zA-Z]/g;
    const variables = this.currentEquation.match(re);
    for (var variable in variables) {
      for (var i = 0; i < this.inputs.length; i++) {
        if (this.inputs[i].name == variables[variable]) {
          substitutedEquation = substitutedEquation.replace(
            this.inputs[i].name,
            this.findIOValue(this.inputs[i].name)
          );
        }
      }
    }
    return GlobalVariables.limitedEvaluate(substitutedEquation);
  }

  /**
   * Create Leva Menu Inputs - returns to ParameterEditor
   */
  createLevaInputs(setInputChanged) {
    // recreate inputs
    let inputParams = {};
    /** Runs through active atom inputs and adds IO parameters to default param*/
    if (this.inputs) {
      this.inputs.map((input) => {
        const checkConnector = () => {
          return input.connectors.length > 0;
        };

        /* Make an input for the equation itself */
        inputParams["equation"] = {
          value: this.currentEquation,
          label: "Current Equation",
          disabled: false,
          onChange: (value) => {
            if (this.currentEquation !== value) {
              this.setEquation(value);
              setInputChanged(this.currentEquation);
            }

            //;
          },
          order: -3,
        };

        /* Make an input for the equation itself */
        inputParams["result"] = {
          value: this.output.value,
          label: "Result",
          disabled: true,
        };

        /* Makes inputs for Io's other than geometry */
        if (input.valueType !== "geometry") {
          inputParams[input.name] = {
            value: input.value,
            disabled: checkConnector(),
            onChange: (value) => {
              input.setValue(value);

              //this.sendToRender();
            },
            order: -2,
          };
        }
        inputParams["Save Equation"] = button(
          (get) => {
            this.setEquation(get("equation"));
          },
          { order: -1 }
        );
      });
      return inputParams;
    }
  }

  /**
   * Evaluate the equation adding and removing inputs as needed
   */
  updateValue() {
    // super.updateValue();
    try {
      this.addAndRemoveInputs();

      if (this.inputs.every((x) => x.ready)) {
        this.decreaseToProcessCountByOne();

        //Evaluate the equation
        this.value = this.evaluateEquation();

        this.output.setValue(this.value);
        this.output.ready = true;
      }
    } catch (err) {
      console.warn(err);
      this.setAlert(err);
    }
  }

  /**
   * Add the equation choice to the object which is saved for this molecule
   */
  serialize(offset = { x: 0, y: 0 }) {
    var superSerialObject = super.serialize(offset);

    //Write the current equation to the serialized object
    superSerialObject.currentEquation = this.currentEquation;

    return superSerialObject;
  }

  /**
   * Set the current equation to be a new value.
   */
  setEquation(newEquation) {
    this.currentEquation = newEquation.trim(); //remove leading and trailing whitespace
    this.updateValue();
  }

  /**
   * Send the value of this atom to the 3D display. Used to display the number
   */
  sendToRender() {
    //Send code to jotcad to render
    //GlobalVariables.writeToDisplay(this.uniqueID);
    console.log("equation");
  }
}

import Atom from "../prototypes/atom.js";
import GlobalVariables from "../js/globalvariables.js";
import { button, LevaInputs } from "leva";
import { Octokit } from "https://esm.sh/octokit@2.0.19";
import { saveAs } from "file-saver";

/**
 * This class creates an atom which supports uploading a .svg file
 */
export default class Export extends Atom {
  /**
   * The constructor function.
   * @param {object} values An array of values passed in which will be assigned to the class as this.x
   */
  constructor(values) {
    super(values);

    /**
     * This atom's name
     * @type {string}
     */
    this.name = "Export";
    /**
     * This atom's type
     * @type {string}
     */
    this.atomType = "Export";
    /**
     * A description of this atom
     * @type {string}
     */
    this.description =
      "Export Atom, let's you choose a type of file to Export.";
    /**
     * This atom's value. Contains the value of the input geometry, not the stl
     * @type {string}
     */
    this.value = null;
    /**
     * The type of file to export
     * @type {string}
     */
    this.type = null;

    this.resolution = 72;

    this.addIO("input", "geometry", this, "geometry", "");
    this.addIO("input", "Resolution (dpi)", this, "number", 72);

    this.addIO("input", "Part Name", this, "string", "Part Name");

    this.addIO("input", "File Type", this, "string", "SVG");

    this.setValues(values);

    this.fileName = null;

    this.importIndex = 0;
  }

  /**
   * Draw the circle atom & icon.
   */
  draw() {
    super.draw(); //Super call to draw the rest

    GlobalVariables.c.beginPath();
    GlobalVariables.c.fillStyle = "#484848";
    GlobalVariables.c.font = `${GlobalVariables.widthToPixels(
      this.radius
    )}px Work Sans Bold`;
    GlobalVariables.c.fillText(
      "G",
      GlobalVariables.widthToPixels(this.x - this.radius / 3),
      GlobalVariables.heightToPixels(this.y) + this.height / 3
    );
    GlobalVariables.c.fill();
    GlobalVariables.c.closePath();
  }

  /**
   * Update the displayed svg file
   */
  updateValue() {
    super.updateValue();

    if (this.inputs.every((x) => x.ready)) {
      this.processing = true;
      let inputID = this.findIOValue("geometry");
      let fileType = this.findIOValue("File Type");

      GlobalVariables.cad
        .visExport(this.uniqueID, inputID, fileType)
        .then((result) => {
          this.basicThreadValueProcessing();
        })
        .catch(this.alertingErrorHandler());
    }
  }

  createLevaInputs() {
    let inputParams = {};
    const exportOptions = ["STL", "SVG", "STEP"];

    /** Runs through active atom inputs and adds IO parameters to default param*/

    if (this.inputs) {
      this.inputs.map((input) => {
        const checkConnector = () => {
          return input.connectors.length > 0;
        };
        if (input.name == "File Type") {
          inputParams[this.uniqueID + "file_ops"] = {
            value: input.value,
            options: exportOptions,
            disabled: checkConnector(),
            label: "File Type",
            onChange: (value) => {
              if (input.value !== value) {
                input.setValue(value);
              }
            },
          };
        }
        /* Makes inputs for Io's other than geometry */
        if (input.name == "Resolution (dpi)") {
          inputParams[this.uniqueID + input.name] = {
            value: input.value,
            label: input.name,
            disabled: this.findIOValue("File Type") != "SVG" ? true : false,
            onChange: (value) => {
              if (input.value !== value) {
                input.setValue(value);
              }
            },
          };
        }
        if (input.name == "Part Name") {
          inputParams[this.uniqueID + input.name] = {
            value: input.value,
            label: input.name,
            disabled: checkConnector(),
            onChange: (value) => {
              if (input.value !== value) {
                input.setValue(value);
              }
            },
          };
        }
      });
    }

    inputParams["Download File"] = button(() =>
      //this.loadFile(importOptions[importIndex])
      this.exportFile()
    );

    return inputParams;
  }

  /**
   * The function which is called when you press the download button.
   */
  exportFile() {
    let fileType = this.findIOValue("File Type");
    let resolution = this.findIOValue("Resolution (dpi)");
    let partName = this.findIOValue("Part Name");

    console.log(this);
    GlobalVariables.cad
      .downExport(
        this.uniqueID,
        fileType,
        resolution,
        GlobalVariables.topLevelMolecule.unitsKey
      )
      .then((result) => {
        saveAs(result, partName + "." + fileType.toLowerCase());
      })
      .catch(this.alertingErrorHandler());
  }
  /**
   * Add the file name to the object which is saved for this molecule
   */
  serialize(offset = { x: 0, y: 0 }) {
    var superSerialObject = super.serialize(offset);
    superSerialObject.type = this.type;
    superSerialObject.resolution = this.resolution;
    superSerialObject.importIndex = this.importIndex;
    superSerialObject.fileName = this.fileName;

    return superSerialObject;
  }
}

import Atom from "../prototypes/atom.js";
import GlobalVariables from "../js/globalvariables.js";

/**
 * The cut away tag adds a tag to a part indicating that it should be cut away from the rest of the model in the next assembly. Essentially it creates a negitive version of itself.
 */
export default class ExtractTag extends Atom {
  /**
   * The constructor function.
   * @param {object} values An array of values passed in which will be assigned to the class as this.x
   */
  constructor(values) {
    super(values);

    /**
     * This atom's type
     * @type {string}
     */
    this.atomType = "Extract Tag";
    /**
     * This atom's type
     * @type {string}
     */
    this.type = "extractTag";
    /**
     * This atom's height as drawn on the screen
     */
    this.height;
    /**
     * This atom's name
     * @type {string}
     */
    this.name = "Extract Tag";
    /**
     * A description of this atom
     * @type {string}
     */
    this.description = "Extracts geometry containing the specified tag.";

    this.addIO("input", "geometry", this, "geometry", null, false, true);
    this.addIO("output", "geometry", this, "geometry", null);

    /** Index for initial tag dropdown
     * @type {number}
     */
    this.tagIndex = 0;

    /** Selected Tag
     * @type {string}
     */
    this.tag;

    this.setValues(values);
  }

  /**
   * Draw the constant which is more rectangular than the regular shape.
   */
  draw() {
    super.draw("rect");
    let pixelsX = GlobalVariables.widthToPixels(this.x);
    let pixelsY = GlobalVariables.heightToPixels(this.y);
    let pixelsRadius = GlobalVariables.widthToPixels(this.radius);
    /**
     * Relates height to radius
     * @type {number}
     */
    this.height = pixelsRadius;

    GlobalVariables.c.beginPath();
    GlobalVariables.c.fillStyle = "#484848";
    GlobalVariables.c.font = `${pixelsRadius}px Work Sans Bold`;
    GlobalVariables.c.fillText(
      String.fromCharCode(0x2191, 0x0040, 0x2191),
      pixelsX - pixelsRadius / 1,
      pixelsY + this.height / 2
    );
    GlobalVariables.c.fill();
    GlobalVariables.c.closePath();
  }

  createLevaInputs() {
    let inputParams = {};
    const tagOptions = GlobalVariables.topLevelMolecule.projectAvailableTags;

    inputParams[this.uniqueID + "tag_ops"] = {
      value: tagOptions[this.tagIndex],
      options: tagOptions,
      label: "Extract Tag",
      onChange: (value) => {
        this.tagIndex = tagOptions.indexOf(value);
        if (this.tag != tagOptions[this.tagIndex]) {
          this.tag = tagOptions[this.tagIndex];
          this.updateValue();
          //this.sendToRender();
        }
      },
    };
    return inputParams;
  }

  /**
   * Adds the cutAway tag to the part
   */
  updateValue() {
    super.updateValue();

    if (this.inputs.every((x) => x.ready)) {
      this.processing = true;
      var inputID = this.findIOValue("geometry");
      var tag = this.tag;

      GlobalVariables.cad
        .extractTag(this.uniqueID, inputID, tag)
        .then(() => {
          this.basicThreadValueProcessing();
        })
        .catch(this.alertingErrorHandler());
    }
  }

  /**
   * Keeps track of tag to be extracted
   */
  serialize(offset = { x: 0, y: 0 }) {
    var superSerialObject = super.serialize(offset);
    superSerialObject.tag = this.tag;
    superSerialObject.tagIndex = this.tagIndex;

    return superSerialObject;
  }
}

