import Atom from '../prototypes/atom.js'
import GlobalVariables from '../js/globalvariables.js'

/**
 * This class creates the Genetic Algorithm atom.
 */
export default class GeneticAlgorithm extends Atom {

    /**
     * The constructor function.
     * @param {object} values An array of values passed in which will be assigned to the class as this.x
     */ 
    constructor(values){

        super(values)

        /**
         * This atom's name
         * @type {string}
         */
        this.name = 'Genetic Algorithm'

        /**
         * This atom's type
         * @type {string}
         */
        this.atomType = 'Genetic Algorithm'
        /** 
         * A description of this atom
         * @type {string}
         */
        this.description = "Defines a new genetic algorithm which will simulate evolution to maximize the fitness function using the input population size and number of generations. Only constants which are upstream of this atom with evolve checked will be changed."

        /**
         * An array of constant objects which need to be evolved
         * @type {array}
         */
        this.constantsToEvolve = []

        /**
         * An array of objects representing the current population
         * @type {array}
         */
        this.population = []

        /**
         * Current individual being evaluated in this generation
         * @type {integer}
         */
        this.individualIndex = 0

        /**
         * Current generation
         * @type {integer}
         */
        this.generation = 0

        /**
         * Top fitness value for the current generation
         * @type {float}
         */
        this.topFitness = 0

        /**
         * A flag to indicate if evolution is in process
         * @type {boolean}
         */
        this.evolutionInProcess = false

        this.addIO('input', 'fitness function', this, 'number', 0)
        this.addIO('input', 'population size', this, 'number', 50)
        this.addIO('input', 'number of generations', this, 'number', 10)

        this.setValues(values)

        this.updateValue()
    }

    /**
     * Draw the code atom which has a code icon.
     */ 
    draw(){

        super.draw() //Super call to draw the rest


        const xInPixels = GlobalVariables.widthToPixels(this.x)
        const yInPixels = GlobalVariables.heightToPixels(this.y)
        const radiusInPixels = GlobalVariables.widthToPixels(this.radius)


        GlobalVariables.c.beginPath()
        GlobalVariables.c.arc(GlobalVariables.widthToPixels(this.x - this.radius/5), 
            GlobalVariables.heightToPixels(this.y), 
            GlobalVariables.widthToPixels(this.radius/2), Math.PI *3.4, Math.PI * 2.7, false) 
        GlobalVariables.c.stroke() 
        GlobalVariables.c.closePath()

        GlobalVariables.c.beginPath()
        GlobalVariables.c.arc(GlobalVariables.widthToPixels(this.x + this.radius/5), 
            GlobalVariables.heightToPixels(this.y ), 
            GlobalVariables.widthToPixels(this.radius/2), Math.PI *3.6, Math.PI * 2.3, true) 
        GlobalVariables.c.stroke() 
        GlobalVariables.c.closePath()  

        GlobalVariables.c.beginPath()
        GlobalVariables.c.fillStyle = '#949294'
        GlobalVariables.c.moveTo(xInPixels - radiusInPixels/3, yInPixels )
        GlobalVariables.c.lineTo(xInPixels + radiusInPixels/3, yInPixels )
        GlobalVariables.c.stroke()
        GlobalVariables.c.closePath()

        GlobalVariables.c.beginPath()
        GlobalVariables.c.fillStyle = '#949294'
        GlobalVariables.c.moveTo(xInPixels - radiusInPixels/3, yInPixels - radiusInPixels/5 )
        GlobalVariables.c.lineTo(xInPixels + radiusInPixels/3, yInPixels - radiusInPixels/5)
        GlobalVariables.c.stroke()
        GlobalVariables.c.closePath()

        GlobalVariables.c.beginPath()
        GlobalVariables.c.fillStyle = '#949294'
        GlobalVariables.c.moveTo(xInPixels - radiusInPixels/3, yInPixels + radiusInPixels/5 )
        GlobalVariables.c.lineTo(xInPixels + radiusInPixels/3, yInPixels + radiusInPixels/5)
        GlobalVariables.c.stroke()
        GlobalVariables.c.closePath()



    }

    /**
     * Generate a layered outline of the part where the tool will cut
     */ 
    updateValue(){
        this.decreaseToProcessCountByOne()
        if(this.evolutionInProcess){
            this.updateSidebar()
            /**
             * Atom is processing
            * @type {boolean}
             */
            this.processing = true
            //Store the result from this individual in it's fitness value
            this.population[this.individualIndex].fitness = this.findIOValue('fitness function')

            //Evaluate the next individual
            this.individualIndex = this.individualIndex + 1
            if(this.individualIndex < this.findIOValue('population size')){
                //Evaluate the next individual by updating all of the inputs
                this.beginEvaluatingIndividual()
            }
            else{
                this.generation = this.generation + 1
                if(this.generation < this.findIOValue('number of generations')){
                    // Generate a new generation from the existing generation and start the process over
                    this.breedAndCullPopulation()
                    this.individualIndex = 0
                    this.beginEvaluatingIndividual()
                }
                else{
                    this.evolutionInProcess = false
                    this.processing = false
                    // Set the inputs to the prime candidate
                    this.population = this.population.sort((a, b) => parseFloat(b.fitness) - parseFloat(a.fitness))
                    this.individualIndex = 0
                    this.generation = 0
                    this.beginEvaluatingIndividual()

                    this.updateSidebar()
                }
            }
        }
    }

    /**
     * Add a button to trigger the evolution process
     */ 
    updateSidebar(){

        if(this.evolutionInProcess){

            //Remove everything in the sidebar
            let sideBar = document.querySelector('.sideBar')
            while (sideBar.firstChild) {
                sideBar.removeChild(sideBar.firstChild)
            }

            //Generate the list
            var sbList = document.createElement('ul')
            sideBar.appendChild(sbList)
            sbList.setAttribute('class', 'sidebar-list')

            //Add text to the list element
            var listElement = document.createElement('LI')
            sbList.appendChild(listElement)

            var labelDiv = document.createElement('div')
            listElement.appendChild(labelDiv)
            var labelText = document.createTextNode("Evolving...   Individual: " + this.individualIndex + ",    Generation: " + this.generation + ",    Best Fitness Value: " + this.topFitness)
            labelDiv.appendChild(labelText)
            labelDiv.setAttribute('class', 'sidebar-subitem label-item')
        }
        else{

            var valueList =  super.updateSidebar() 

            this.createButton(valueList,this,'Evolve',() => {
                this.evolutionInProcess = true

                //Generate a list of all the constants to evolve
                this.updateConstantsList()

                //Generate a population from those constants
                this.initializePopulation()

                //Evaluate the first individual
                this.beginEvaluatingIndividual()
            })
        }
    }

    /**
     * Trigger the process to evaluate the current individual
     */ 
    beginEvaluatingIndividual(){
        const individualToEvaluate = this.population[this.individualIndex]

        //Lock all the constants
        individualToEvaluate.genome.forEach(gene => {
            gene.constantAtom.output.waitOnComingInformation()
        })

        //Set all of their values
        individualToEvaluate.genome.forEach(gene => {
            gene.constantAtom.output.value = gene.newValue
        })

        //Trigger them to update
        individualToEvaluate.genome.forEach(gene => {
            gene.constantAtom.updateValue()
        })
    }

    /**
     * Generate a random number between min and max
     */ 
    getRandomValue(min, max) {
        const randomVal = Math.random() * (max - min) + min
        return randomVal
    }

    /**
     * Generate an initial population
     */ 
    initializePopulation(){
        this.population = []
        this.individualIndex = 0
        this.generation = 0
        this.topFitness = 0

        var i = 0
        while(i < this.findIOValue('population size')){
            var genome = []
            this.constantsToEvolve.forEach(constant => {
                var gene = {
                    newValue: this.getRandomValue(constant.min, constant.max),
                    constantAtom: constant
                }
                genome.push(gene)
            })

            //Generate an individual with a random value for each input
            var individual = {
                genome: genome,
                fitness: null
            }
            this.population.push(individual)

            i++
        }
    }

    /**
     * Take two individuals and breed them to form a new individual with a mix of their genes and mutations
     */ 
    breedTwo(A, B){
        const lengthOfGenome = A.genome.length

        var child = {
            fitness: null,
            genome: []
        }

        var geneIndex = 0
        while(geneIndex < lengthOfGenome){
            var newGene = {
                newValue: null,
                constantAtom: A.genome[geneIndex].constantAtom
            }


            const maxVal         = A.genome[geneIndex].constantAtom.max
            const minVal         = A.genome[geneIndex].constantAtom.min
            const mutationAmount = 0.1*(maxVal-minVal)*(Math.random()-.5) //Mutate by at most +-.5%

            var newGeneVal = null
            if(Math.random() > 0.5){
                newGeneVal = A.genome[geneIndex].newValue+mutationAmount
            }else{
                newGeneVal = B.genome[geneIndex].newValue+mutationAmount
            }

            //Constrain to within bounds
            newGene.newValue = Math.min(Math.max(newGeneVal, minVal), maxVal)

            child.genome.push(newGene)

            geneIndex++
        }
        return child
    }

    /**
     * Breed the best performers in the population, cull the rest
     */ 
    breedAndCullPopulation(){
        this.population = this.population.sort((a, b) => parseFloat(b.fitness) - parseFloat(a.fitness))

        this.topFitness = this.population[0].fitness

        // Create a new population by taking the top 1/5th of the original population
        const keptPopulationNumber = Math.round(this.population.length / 5)

        const breeders = this.population.slice(0,keptPopulationNumber)

        //Generate a new population of individuals by breading from the last generation
        var newGeneration = []
        var index = 0
        while(index < this.population.length - keptPopulationNumber){
            const individualOneIndex = Math.round(Math.random()*(breeders.length-1))
            const individualTwoIndex = Math.round(Math.random()*(breeders.length-1))
            const newIndividual = this.breedTwo(breeders[individualOneIndex], breeders[individualTwoIndex])
            newGeneration.push(newIndividual)
            index = index + 1
        }

        this.population = breeders.concat(newGeneration)
    }

    /**
     * Regenerate the list of constants we are evolving
     */ 
    updateConstantsList(){
        //Create an array of the inputs by walking up stream
        this.constantsToEvolve = []
        this.inputs.forEach(input => {
            input.connectors.forEach(connector => {
                connector.walkBackForConstants(constantObject => {this.addToConstantsList(constantObject)})
            })
        })
    }

    /**
     * Add a constant to the list. Used as a callback from passing up the tree.
     */ 
    addToConstantsList(constantObject){
        this.constantsToEvolve.push(constantObject)
    }

}

import Molecule from "../molecules/molecule";
import GlobalVariables from "../js/globalvariables.js";
import { Octokit } from "https://esm.sh/octokit@2.0.19";
import { button } from "leva";
import { re } from "mathjs";
import { LevaInputs } from "leva";

/**
 * This class creates the GitHubMolecule atom.
 */
export default class GitHubMolecule extends Molecule {
  /**
   * The constructor function.
   * @param {object} values An array of values passed in which will be assigned to the class as this.x
   */
  constructor(values) {
    super(values);

    /**
     * This atom's name
     * @type {string}
     */
    this.name = "Github Molecule";
    /**
     * This atom's type
     * @type {string}
     */
    this.atomType = "GitHubMolecule";
    /**
     * A flag to signal if this node is the top level node
     * @type {boolean}
     */
    this.topLevel = false;
    /**
     * The color for the whole in the center of the drawing...probably doesn't need to be in this scope
     * @type {string}
     */
    this.centerColor = "black";
    /**
     * A description of this atom
     * @type {string}
     */
    this.description = "Project imported from GitHub";

    this.gitHubUniqueID;

    this.setValues(values);
  }

  /**
     * This replaces the default Molecule double click behavior to prevent you from being able to double click into a github molecule
     * @param {number} x - The x coordinate of the click
     * @param {number} y - The y coordinate of the click
     // */
  doubleClick(x, y) {
    var clickProcessed = false;
    var distFromClick = GlobalVariables.distBetweenPoints(x, this.x, y, this.y);
    if (distFromClick < this.radius) {
      clickProcessed = true;
    }
    return clickProcessed;
  }

  /**
   * Create Leva Menu Input - returns to ParameterEditor
   */
  createLevaInputs() {
    let inputParams = {};

    /** Runs through active atom inputs and adds IO parameters to default param*/
    if (this.inputs) {
      this.inputs.map((input) => {
        const checkConnector = () => {
          return input.connectors.length > 0;
        };

        /* Makes inputs for Io's other than geometry */
        if (input.valueType !== "geometry") {
          inputParams[this.uniqueID + input.name] = {
            value: input.value,
            label: input.name,
            disabled: checkConnector(),
            onChange: (value) => {
              if (input.value !== value) {
                input.setValue(value);
                //this.sendToRender();
              }
            },
          };
          if (input.type && input.valueType) {
            inputParams[this.uniqueID + input.name].type =
              LevaInputs[input.valueType.toUpperCase()];
          }
        }
        if (input.type && input.valueType) {
          inputParams[this.uniqueID + input.name].type =
            LevaInputs[input.valueType.toUpperCase()];
        }
      });
      inputParams["Reload From Github"] = button(() =>
        this.reloadMoleculeFromGithub()
      );
      return inputParams;
    }
  }

  /**
   * Reload this github molecule from github
   */
  reloadMoleculeFromGithub() {
    var githubMoleculeObjectPreReload = this.serialize();
    var githubMoleculeParentObjectConnectorsPreReload =
      this.parent.serialize().allConnectors;

    const copyOfNodeToBeDeleted = this;
    copyOfNodeToBeDeleted.deleteNode(false, false, true);

    let item = this.parentRepo;

    this.loadGithubMoleculeByName(
      /*old way > keeping until i fix reload -- this.gitHubUniqueID*/
      item,
      githubMoleculeObjectPreReload,
      githubMoleculeParentObjectConnectorsPreReload
    );
  }
}

import Atom from '../prototypes/atom.js'
import { addOrDeletePorts } from '../js/alwaysOneFreeInput.js'
import GlobalVariables from '../js/globalvariables.js'

/**
 * This class creates the group atom instance.
 */
export default class Group extends Atom{
    /**
    * Creates a new group atom.
    * @param {object} values - An object of values. Each of these values will be applied to the resulting atom.
    */
    constructor(values){
        super(values)

        this.addIO('output', 'geometry', this, 'geometry', '')

        /**
         * This atom's name
         * @type {string}
         */
        this.name = 'Group'
        /**
         * This atom's type
         * @type {string}
         */
        this.atomType = 'Group'
        /**
         * A list of all of the inputs to this molecule. May be loaded when the molecule is created.
         * @type {array}
         */
        this.ioValues = []
        /**
         * A flag to determine if cutaway geometry is removed.
         * @type {boolean}
         */
        this.removeCutawayGeometry = true
        /** 
         * A description of this atom
         * @type {string}
         */
        this.description = "Groups shapes together into one. Shapes are allowed to overlap."

        this.setValues(values)

        //This loads any inputs which this atom had when last saved.
        if (typeof this.ioValues !== 'undefined'){
            this.ioValues.forEach(ioValue => { //for each saved value
                this.addIO('input', ioValue.name, this, 'geometry', '')
            })
        }

        this.setValues([])
    }

    /**
     * Add or delete ports as needed in addition to the normal begin propogation stuff
     */ 
    beginPropagation(){

        addOrDeletePorts(this)  //Add or remove ports as needed

        super.beginPropagation()
    }

    /**
     * Draw the group icon
     */ 
    draw(){

        super.draw() //Super call to draw the rest

        const xInPixels = GlobalVariables.widthToPixels(this.x)
        const yInPixels = GlobalVariables.heightToPixels(this.y)
        const radiusInPixels = GlobalVariables.widthToPixels(this.radius)

        GlobalVariables.c.beginPath()
        GlobalVariables.c.fillStyle = '#949294'
        GlobalVariables.c.moveTo(xInPixels - radiusInPixels/2, yInPixels + radiusInPixels/2)
        GlobalVariables.c.lineTo(xInPixels + radiusInPixels/2, yInPixels + radiusInPixels/2)
        GlobalVariables.c.lineTo(xInPixels + radiusInPixels/2, yInPixels)
        GlobalVariables.c.lineTo(xInPixels - radiusInPixels/2, yInPixels)
        GlobalVariables.c.lineTo(xInPixels - radiusInPixels/2, yInPixels + radiusInPixels/2)
        //GlobalVariables.c.fill()
        GlobalVariables.c.stroke()
        GlobalVariables.c.closePath()
        GlobalVariables.c.beginPath()
        GlobalVariables.c.lineTo(xInPixels + radiusInPixels/4, yInPixels - radiusInPixels/2)
        GlobalVariables.c.lineTo(xInPixels - radiusInPixels/4, yInPixels - radiusInPixels/2)
        GlobalVariables.c.lineTo(xInPixels - radiusInPixels/4, yInPixels)
        GlobalVariables.c.lineTo(xInPixels + radiusInPixels/2, yInPixels)
        GlobalVariables.c.lineTo(xInPixels + radiusInPixels/4, yInPixels - radiusInPixels/2)

        //GlobalVariables.c.fill()
        GlobalVariables.c.lineWidth = 1
        GlobalVariables.c.lineJoin = "round"
        GlobalVariables.c.stroke()
        GlobalVariables.c.closePath()

    }

    /**
    * Super class the default update value function. This function computes creates an array of all of the input values and then passes that array to a worker thread to create the group.
    */ 
    updateValue(){
        if(this.inputs.every(x => x.ready)){
            try{
                var inputValues = []
                this.inputs.forEach( io => {
                    if(io.connectors.length > 0 && io.type == 'input'){
                        inputValues.push(io.getValue())
                    }
                })

                const values = { op: "group",paths: inputValues, writePath: this.path }

                this.basicThreadValueProcessing(values, "group")
                this.clearAlert()
            }catch(err){this.setAlert(err)}

            //Delete or add ports as needed
            addOrDeletePorts(this)
        }
    }

    /**
    * Super class the default serialize function to save the inputs since this atom has variable numbers of inputs.
    */ 
    serialize(savedObject){
        var thisAsObject = super.serialize(savedObject)

        var ioValues = []
        this.inputs.forEach(io => {
            if (io.connectors.length > 0){
                var saveIO = {
                    name: io.name,
                    ioValue: io.getValue()
                }
                ioValues.push(saveIO)
            }
        })

        thisAsObject.ioValues = ioValues

        return thisAsObject
    }
}

import Atom from "../prototypes/atom";
import GlobalVariables from "../js/globalvariables.js";

/**
 * This class creates the input atom.
 */
export default class Input extends Atom {
  /**
   * The constructor function.
   * @param {object} values An array of values passed in which will be assigned to the class as this.x
   */
  constructor(values) {
    super(values);

    /**
     * This atom's name
     * @type {string}
     */
    this.name;
    /**
     * A description of this atom
     * @type {string}
     */
    this.description =
      "Adds an input to the parent molecule. If the parent molecule is the top level of the project then the input will be available when the project is shared or imported into another project. Name is editable";
    /**
     * The value the input is set to, defaults to 10. Is this still used or are we using the value of the attachmentPoint now?
     * @type {number}
     */
    this.value = 10;
    /**
     * This atom's type
     * @type {string}
     */
    this.atomType = "Input";
    /**
     * This atom's height for drawing
     * @type {number}
     */
    this.height;

    this.type = "number";
    /**
     * This atom's old name, used during name changes
     * @type {string}
     */
    this.oldName = this.name;

    this.radius = this.radius * 1.3;

    this.addIO("output", "number or geometry", this, this.type, this.value);

    //Add a new input to the current molecule
    if (typeof this.parent !== "undefined") {
      this.parent.addIO("input", this.name, this.parent, this.type, this.value);
    }

    this.setValues(values);
  }

  /** Solution to canvas overflow https://stackoverflow.com/questions/10508988/html-canvas-text-overflow-ellipsis*/
  fittingString(c, str, maxWidth) {
    var width = c.measureText(str).width;
    var ellipsis = "";
    var ellipsisWidth = c.measureText(ellipsis).width;
    if (width <= maxWidth || width <= ellipsisWidth) {
      return str;
    } else {
      var len = str.length;
      while (width >= maxWidth - ellipsisWidth && len-- > 0) {
        str = str.substring(0, len);
        width = c.measureText(str).width;
      }
      return str + ellipsis;
    }
  }

  /**
   * Draws the atom on the screen.
   */
  draw() {
    // //Snap the inputs to the far right side
    /**
     * The x position of the atom
     * @type {number}
     */
    this.x = 0.04;

    let xInPixels = GlobalVariables.widthToPixels(this.x);
    let yInPixels = GlobalVariables.heightToPixels(this.y);
    let radiusInPixels = GlobalVariables.widthToPixels(this.radius);
    /**
     * Relates height to radius
     * @type {number}
     */
    this.height = radiusInPixels;
    //Check if the name has been updated
    if (this.name != this.oldName) {
      this.updateParentName();
    }

    //Set colors
    if (this.processing) {
      GlobalVariables.c.fillStyle = "blue";
    } else if (this.selected) {
      GlobalVariables.c.fillStyle = this.selectedColor;
      GlobalVariables.c.strokeStyle = this.defaultColor;
      /**
       * This background color
       * @type {string}
       */
      this.color = this.selectedColor;
      /**
       * This atoms accent color
       * @type {string}
       */
      this.strokeColor = this.defaultColor;
    } else {
      GlobalVariables.c.fillStyle = this.defaultColor;
      GlobalVariables.c.strokeStyle = this.selectedColor;
      this.color = this.defaultColor;
      this.strokeColor = this.selectedColor;
    }

    this.inputs.forEach((input) => {
      input.draw();
    });
    if (this.output) {
      this.output.draw();
    }
    GlobalVariables.c.beginPath();
    GlobalVariables.c.moveTo(0, yInPixels + this.height / 2);
    GlobalVariables.c.lineTo(55, yInPixels + this.height / 2);
    GlobalVariables.c.lineTo(xInPixels + radiusInPixels, yInPixels);
    GlobalVariables.c.lineTo(55, yInPixels - this.height / 2);
    GlobalVariables.c.lineTo(0, yInPixels - this.height / 2);
    GlobalVariables.c.lineWidth = 1;
    GlobalVariables.c.fill();
    GlobalVariables.c.closePath();
    GlobalVariables.c.stroke();
    GlobalVariables.c.font = "11px Work Sans";
    GlobalVariables.c.textAlign = "start";
    GlobalVariables.c.fillStyle = "black";
    GlobalVariables.c.width = 20;
    GlobalVariables.c.textOverflow = "ellipsis";

    GlobalVariables.c.fillText(
      this.fittingString(GlobalVariables.c, this.name, 50),
      5,
      yInPixels + 3
    );
  }

  /**
   * Remove the input from the parent molecule, then delete the atom normally.
   */
  deleteNode(backgroundClickAfter = true, deletePath = true, silent = false) {
    //Remove this input from the parent molecule
    if (typeof this.parent !== "undefined") {
      this.parent.removeIO("input", this.name, this.parent, silent);
    }

    super.deleteNode(backgroundClickAfter, deletePath, silent);
  }

  /**
   * Called when the name has changed to updated the name of the parent molecule IO
   */
  updateParentName() {
    //Run through the parent molecule and find the input with the same name
    this.parent.inputs.forEach((child) => {
      if (child.name == this.oldName) {
        child.name = this.name;
      }
    });
    this.oldName = this.name;
  }

  /**
   * Grabs the new value from the parent molecule's input, sets this atoms value, then propagates.
   */
  updateValue() {
    this.parent.inputs.forEach((input) => {
      //Grab the value for this input from the parent's inputs list
      if (input.name == this.name) {
        //If we have found the matching input
        this.decreaseToProcessCountByOne();
        this.value = input.getValue();
        this.output.waitOnComingInformation(); //Lock all of the dependents
        this.output.setValue(this.value);
        this.parent.updateIO(
          "input",
          this.name,
          this.parent,
          this.type,
          this.value
        );
      }
    });
  }
  /**
   * Create Leva Menu Inputs for Editable Input Names - returns to ParameterEditor
   */
  createLevaInputs() {
    let inputNames = {};
    inputNames[this.uniqueID] = {
      value: this.name,
      label: "Input Name",
      disabled: false,
      onChange: (value) => {
        if (this.name !== value) {
          this.name = value;
        }
      },
    };
    inputNames[this.uniqueID + "type"] = {
      value: this.type,
      label: "Input Type",
      disabled: false,
      options: ["number", "string", "geometry", "array"],
      onChange: (value) => {
        if (this.type !== value) {
          this.type = value;
          this.output.valueType = value;
          //Add a new input to the current molecule
          if (typeof this.parent !== "undefined") {
            this.parent.updateIO(
              "input",
              this.name,
              this.parent,
              this.type,
              this.value
            );
          }
        }
      },
    };
    return inputNames;
  }
  /**
   * Returns the current value being output
   */
  getOutput() {
    return this.output.getValue();
  }

  /**
   * Add the input Type choice to the object which is saved for this molecule
   */
  serialize(offset = { x: 0, y: 0 }) {
    var superSerialObject = super.serialize(offset);

    //Write the current color selection to the serialized object
    superSerialObject.type = this.type;

    return superSerialObject;
  }
}

import Atom from "../prototypes/atom";
import GlobalVariables from "../js/globalvariables.js";

/**
 * This class creates the intersection atom.
 */
export default class Intersection extends Atom {
  /**
   * The constructor function.
   * @param {object} values An array of values passed in which will be assigned to the class as this.x
   */
  constructor(values) {
    super(values);

    this.addIO("input", "geometry1", this, "geometry", "");
    this.addIO("input", "geometry2", this, "geometry", "");
    this.addIO("output", "geometry", this, "geometry", "");
    /**
     * This atom's name
     * @type {string}
     */
    this.name = "Intersection";
    /**
     * This atom's type
     * @type {string}
     */
    this.atomType = "Intersection";
    /**
     * A description of this atom
     * @type {string}
     */
    this.description = "The space shared by two shapes.";

    this.setValues(values);
  }

  /**
   * Draw the rectangle atom & icon.
   */
  draw() {
    super.draw(); //Super call to draw the rest

    const xInPixels = GlobalVariables.widthToPixels(this.x);
    const yInPixels = GlobalVariables.heightToPixels(this.y);
    const radiusInPixels = GlobalVariables.widthToPixels(this.radius);

    GlobalVariables.c.beginPath();
    GlobalVariables.c.fillStyle = "#949294";
    GlobalVariables.c.moveTo(
      xInPixels - radiusInPixels / 2,
      yInPixels + radiusInPixels / 2
    );
    GlobalVariables.c.lineTo(
      xInPixels + radiusInPixels / 2,
      yInPixels + radiusInPixels / 2
    );
    GlobalVariables.c.lineTo(xInPixels + radiusInPixels / 2, yInPixels);
    GlobalVariables.c.lineTo(xInPixels + radiusInPixels / 4, yInPixels);
    GlobalVariables.c.lineTo(
      xInPixels + radiusInPixels / 4,
      yInPixels - radiusInPixels / 2
    );
    GlobalVariables.c.lineTo(
      xInPixels - radiusInPixels / 4,
      yInPixels - radiusInPixels / 2
    );
    GlobalVariables.c.lineTo(xInPixels - radiusInPixels / 4, yInPixels);
    GlobalVariables.c.lineTo(xInPixels - radiusInPixels / 2, yInPixels);
    GlobalVariables.c.lineTo(
      xInPixels - radiusInPixels / 2,
      yInPixels + radiusInPixels / 2
    );
    GlobalVariables.c.fill();
    GlobalVariables.c.lineWidth = 1;
    GlobalVariables.c.lineJoin = "round";
    GlobalVariables.c.stroke();
    GlobalVariables.c.closePath();
  }

  /**
   * Grab the input geometries and pass them to a worker thread for computation.
   */
  updateValue() {
    super.updateValue();

    if (this.inputs.every((x) => x.ready)) {
      this.processing = true;
      const input1ID = this.findIOValue("geometry1");
      const input2ID = this.findIOValue("geometry2");

      GlobalVariables.cad
        .intersect(this.uniqueID, input1ID, input2ID)
        .then(() => {
          this.basicThreadValueProcessing();
        })
        .catch(this.alertingErrorHandler());
    }
  }
}

import Atom from "../prototypes/atom.js";
