    this.height = pixelsRadius;

    GlobalVariables.c.beginPath();
    GlobalVariables.c.fillStyle = "#484848";
    GlobalVariables.c.font = `${pixelsRadius * 1.3}px Work Sans Bold`;
    GlobalVariables.c.fillText(
      "@",
      GlobalVariables.widthToPixels(this.x - this.radius / 1.5),
      GlobalVariables.heightToPixels(this.y) + this.height / 1.5
    );
    GlobalVariables.c.fill();
    GlobalVariables.c.closePath();
  }

  checkHasTag() {
    return this.cutTag;
  }

  addTagsToAvailableTags() {
    let newProjectTags = Array.from(
      new Set(
        GlobalVariables.topLevelMolecule.projectAvailableTags.concat(this.tags)
      )
    );
    GlobalVariables.topLevelMolecule.projectAvailableTags = newProjectTags;
  }

  createLevaInputs() {
    let inputParams = {};

    inputParams[this.uniqueID + "cut_string"] = {
      value: this.cutTag,
      label: "Cut List Tag",
      onChange: (value) => {
        if (this.cutTag !== value) {
          if (value === true) {
            this.cutTag = true;
            this.tags.push("cutLayout");
            this.name = this.tags.toString();
            this.updateValue();
          } else {
            this.cutTag = false;
            this.tags = this.tags.filter((e) => e !== "cutLayout");
            this.name = this.tags.toString();
            this.updateValue();
          }
        }
      },
    };
    inputParams[this.uniqueID + "custom_string"] = {
      value: this.tags.filter((e) => e !== "cutLayout")[0],
      label: "Add Tag",
      disabled: false,
      onChange: (value) => {
        this.tags = this.cutTag ? ["cutLayout"] : [];
        this.tags.push(value);
        this.name = this.tags.toString();
        this.updateValue();
      },
    };

    return inputParams;
  }
  /**
   * Add a tag to the input geometry. The substance is not changed.
   */
  updateValue() {
    super.updateValue();

    if (this.inputs.every((x) => x.ready)) {
      this.processing = true;
      var inputID = this.findIOValue("geometry");
      var tags = this.tags;
      this.addTagsToAvailableTags();
      GlobalVariables.cad
        .tag(this.uniqueID, inputID, tags)
        .then(() => {
          this.basicThreadValueProcessing();
        })
        .catch(this.alertingErrorHandler());
    }
  }
  /**
   * Send the value of this atom to the 3D display. Used to display the number
   */
  sendToRender() {
    // do nothing
    console.log("tag has nothing to render");
  }

  /**
   * Add the file name to the object which is saved for this molecule
   */
  serialize(offset = { x: 0, y: 0 }) {
    var superSerialObject = super.serialize(offset);
    superSerialObject.tags = this.tags;
    superSerialObject.cutTag = this.cutTag;

    return superSerialObject;
  }
}

import Atom from "../prototypes/atom.js";
import GlobalVariables from "../js/globalvariables.js";
import Fonts from "../js/fonts.js";

/**
 * This class creates the circle atom.
 */
export default class Text extends Atom {
  /**
   * The constructor function.
   * @param {object} values An array of values passed in which will be assigned to the class as this.x
   */
  constructor(values) {
    super(values);

    /**
     * This atom's name
     * @type {string}
     */
    this.name = "Text";
    /**
     * This atom's type
     * @type {string}
     */
    this.atomType = "Text";
    /**
     * A description of this atom
     * @type {string}
     */
    this.description = "Creates a new text sketch.";

    this.fontFamily = "ROBOTO";

    /**
     * The index of the currently selected color option.
     * @type {number}
     */
    this.selectedFontIndex = 0;

    this.availableFonts = Fonts;

    this.addIO("input", "Font Size", this, "number", 10.0);
    this.addIO("input", "Text", this, "string", "Lorem Ipsum");
    this.addIO("output", "geometry", this, "geometry", "");

    this.setValues(values);
  }

  /**
   * Starts propagation from this atom if it is not waiting for anything up stream.
   */
  beginPropagation(force = false) {
    //Triggers inputs with nothing connected to begin propagation
    this.inputs.forEach((input) => {
      input.beginPropagation();
    });
  }

  /**
   * Draw the circle atom & icon.
   */
  draw() {
    super.draw(); //Super call to draw the rest

    GlobalVariables.c.beginPath();
    GlobalVariables.c.fillStyle = "#484848";
    GlobalVariables.c.font = `${GlobalVariables.widthToPixels(
      this.radius
    )}px Work Sans Bold`;
    GlobalVariables.c.fillText(
      "T",
      GlobalVariables.widthToPixels(this.x - this.radius / 3),
      GlobalVariables.heightToPixels(this.y) + this.height / 3
    );
    GlobalVariables.c.fill();
    GlobalVariables.c.closePath();
  }

  createLevaInputs() {
    let inputParams = {};

    /** Runs through active atom inputs and adds IO parameters to default param*/
    if (this.inputs) {
      this.inputs.map((input) => {
        const checkConnector = () => {
          return input.connectors.length > 0;
        };

        /* Makes inputs for Io's other than geometry */
        if (input.valueType !== "geometry") {
          inputParams[this.uniqueID + input.name] = {
            value: input.value,
            label: input.name,
            step: 0.25,
            disabled: checkConnector(),
            onChange: (value) => {
              if (input.value !== value) {
                input.setValue(value);
                //this.sendToRender();
              }
            },
          };
        }
      });
    }
    const fontOptions = Fonts;

    inputParams[this.uniqueID + "FontFamily"] = {
      value: Object.keys(fontOptions)[this.selectedFontIndex],
      label: "Font Family",
      options: Object.keys(fontOptions),
      onChange: (value) => {
        if (value != this.fontFamily) {
          this.selectedFontIndex = Object.keys(fontOptions).indexOf(value);
          this.fontFamily = Object.keys(fontOptions)[this.selectedFontIndex];
          this.updateValue();
        }
      },
    };

    return inputParams;
  }

  /**
   * Update the value of the circle in worker.
   */
  updateValue() {
    super.updateValue();
    var fontSize = this.findIOValue("Font Size");
    var text = this.findIOValue("Text");
    let fontFamily = this.fontFamily;
    GlobalVariables.cad
      .text(this.uniqueID, text, fontSize, fontFamily)
      .then(() => {
        this.basicThreadValueProcessing();
      })
      .catch(this.alertingErrorHandler());
  }

  serialize(offset = { x: 0, y: 0 }) {
    var thisAsObject = super.serialize(offset);

    var ioValues = [];
    this.inputs.forEach((io) => {
      if (io.connectors.length > 0) {
        var saveIO = {
          name: io.name,
          ioValue: io.getValue(),
        };
        ioValues.push(saveIO);
      }
    });

    thisAsObject.ioValues = ioValues;
    thisAsObject.fontFamily = this.fontFamily;
    thisAsObject.selectedFontIndex = this.selectedFontIndex;

    return thisAsObject;
  }
}

import AttachmentPoint from "./attachmentpoint";
import GlobalVariables from "../js/globalvariables.js";
import showdown from "showdown";
import globalvariables from "../js/globalvariables.js";

/**
 * This class is the prototype for all atoms.
 */
export default class Atom {
  /**
   * The constructor function.
   * @param {object} values An array of values passed in which will be assigned to the class as this.x
   */
  constructor(values) {
    //Setup default values
    /**
     * An array of all of the input attachment points connected to this atom
     * @type {array}
     */
    this.inputs = [];
    /**
     * This atom's output attachment point if it has one
     * @type {object}
     */
    this.output = null;
    /**
     * This atom's unique ID. Often overwritten later when loading
     * @type {number}
     */
    this.uniqueID = GlobalVariables.generateUniqueID();
    /**
     * A description of this atom
     * @type {string}
     */
    this.description = "none";
    /**
     * The X cordinate of this atom
     * @type {number}
     */
    this.x = 0;
    /**
     * The Y cordinate of this atom
     * @type {number}
     */
    this.y = 0;
    /**
     * This atom's radius as displayed on the screen is 1/72 width
     * @type {number}
     */
    this.radius = 1 / 75;
    /**
     * This atom's default color (ie when not selected or processing)
     * @type {string}
     */
    this.defaultColor = "#F3EFEF";
    /**
     * This atom's color when selected
     * @type {string}
     */
    this.selectedColor = "#484848";
    /**
     * The color currently used for strokes
     * @type {string}
     */
    this.strokeColor = "#484848";
    /**
     * A flag to indicate if this atom is currently selected
     * @type {boolean}
     */
    this.selected = false;
    /**
     * This atom's current color
     * @type {string}
     */
    this.color = "#F3EFEF";
    /**
     * This atom's name
     * @type {string}
     */
    this.name = "name0";
    /**
     * This atom's parent, usually the molecule which contains this atom
     * @type {object}
     */
    this.parentMolecule = null;
    /**
     * This atom's value...Is can this be done away with? Are we basically storing the value in the output now?
     * @type {object}
     */
    this.value = null;
    /**
     * A flag to indicate if this atom is currently being dragged on the screen.
     * @type {boolean}
     */
    this.isMoving = false;
    /**
     * A flag to indicate if we are hovering over this atom.
     * @type {boolean}
     */
    this.showHover = false;
    /**
     * The X coordinate of this atom now
     * @type {number}
     */
    this.x = 0;
    /**
     * The Y coordinate of this atom now
     * @type {number}
     */
    this.y = 0;
    /**
     * A warning message displayed next to the atom. Put text in here to have a warning automatically show up. Cleared each time the output is regenerated.
     * @type {string}
     */
    this.alertMessage = "";
    /**
     * A flag to indicate if the atom is currently computing a new output. Turns the molecule blue.
     * @type {boolean}
     */
    this.processing = false;

    for (var key in values) {
      /**
       * Assign each of the values in values as this.value
       */
      this[key] = values[key];
    }
  }

  /**
   * Applies each of the passed values to this as this.x
   * @param {object} values - A list of values to set
   */
  setValues(values) {
    //Assign the object to have the passed in values

    for (var key in values) {
      this[key] = values[key];
    }

    if (typeof this.ioValues !== "undefined") {
      this.ioValues.forEach((ioValue) => {
        //for each saved value
        this.inputs.forEach((io) => {
          //Find the matching IO and set it to be the saved value
          if (ioValue.name == io.name && io.type == "input") {
            io.value = ioValue.ioValue;
          }
        });
      });
    }
  }

  /**
   * Draws the atom on the screen
   */
  draw(drawType) {
    let xInPixels = GlobalVariables.widthToPixels(this.x);
    let yInPixels = GlobalVariables.heightToPixels(this.y);
    let radiusInPixels = GlobalVariables.widthToPixels(this.radius);

    this.inputs.forEach((child) => {
      child.draw();
    });

    GlobalVariables.c.beginPath();
    GlobalVariables.c.font = "10px Work Sans";

    if (this.processing) {
      GlobalVariables.c.fillStyle = "blue";
    } else if (this.selected) {
      GlobalVariables.c.fillStyle = this.selectedColor;
      GlobalVariables.c.strokeStyle = this.selectedColor;
      this.color = this.selectedColor;
      this.strokeColor = this.defaultColor;
    } else {
      GlobalVariables.c.fillStyle = this.defaultColor;
      GlobalVariables.c.strokeStyle = this.selectedColor;
      this.color = this.defaultColor;
      this.strokeColor = this.selectedColor;
    }

    GlobalVariables.c.beginPath();
    if (drawType == "rect") {
      GlobalVariables.c.rect(
        xInPixels - radiusInPixels * 1.25,
        yInPixels - this.height / 1.5,
        2.5 * radiusInPixels,
        this.height * 1.25
      );
    } else if (drawType == "square") {
      GlobalVariables.c.rect(
        xInPixels - radiusInPixels,
        yInPixels - radiusInPixels,
        2 * radiusInPixels,
        2 * radiusInPixels
      );
    } else {
      GlobalVariables.c.arc(
        xInPixels,
        yInPixels,
        radiusInPixels,
        0,
        Math.PI * 2,
        false
      );
    }
    GlobalVariables.c.textAlign = "start";
    GlobalVariables.c.fill();
    GlobalVariables.c.strokeStyle = this.strokeColor;
    GlobalVariables.c.fillStyle = "white";
    GlobalVariables.c.stroke();
    GlobalVariables.c.closePath();

    GlobalVariables.c.beginPath();
    GlobalVariables.c.textAlign = "start";
    GlobalVariables.c.fillText(
      this.name,
      xInPixels + radiusInPixels,
      yInPixels - radiusInPixels
    );
    GlobalVariables.c.fill();
    GlobalVariables.c.strokeStyle = this.strokeColor;
    GlobalVariables.c.lineWidth = 1;
    GlobalVariables.c.stroke();
    GlobalVariables.c.closePath();

    if (this.showHover) {
      if (this.alertMessage.length > 0) {
        this.color = "red";

        //Draw Alert block
        GlobalVariables.c.beginPath();
        const padding = 10;
        GlobalVariables.c.fillStyle = "red";
        GlobalVariables.c.rect(
          xInPixels + radiusInPixels - padding / 2,
          yInPixels - radiusInPixels + padding / 2,
          GlobalVariables.c.measureText(this.alertMessage.toUpperCase()).width +
            padding,
          -(parseInt(GlobalVariables.c.font) + padding)
        );
        GlobalVariables.c.fill();
        GlobalVariables.c.strokeStyle = "black";
        GlobalVariables.c.lineWidth = 1;
        GlobalVariables.c.stroke();
        GlobalVariables.c.closePath();

        GlobalVariables.c.beginPath();
        GlobalVariables.c.fillStyle = "black";
        GlobalVariables.c.fillText(
          this.alertMessage.toUpperCase(),
          xInPixels + radiusInPixels,
          yInPixels - radiusInPixels
        );
        GlobalVariables.c.closePath();
      }
    }
  }

  /**
   * Adds a new attachment point to this atom
   * @param {boolean} type - The type of the IO (input or output)
   * @param {string} name - The name of the new attachment point
   * @param {object} target - The atom to attach the new attachment point to. Should we force this to always be this one?
   * @param {string} valueType - Describes the type of value the input is expecting options are number, geometry, array
   * @param {object} defaultValue - The default value to be used when the value is not yet set
   */
  addIO(type, name, target, valueType, defaultValue, ready, primary = false) {
    //compute the baseline offset from parent node
    if (
      target.inputs.find((o) => o.name === name && o.type === type) == undefined
    ) {
      var offset;
      if (type == "input") {
        offset = -1 * target.scaledRadius;
      } else {
        offset = target.scaledRadius;
      }
      var newAp = new AttachmentPoint({
        parentMolecule: target,
        defaultOffsetX: offset,
        defaultOffsetY: 0,
        type: type,
        valueType: valueType,
        name: name,
        primary: primary,
        value: defaultValue,
        defaultValue: defaultValue,
        uniqueID: GlobalVariables.generateUniqueID(),
        atomType: "AttachmentPoint",
        ready: true,
      });

      if (type == "input") {
        target.inputs.push(newAp);
      } else {
        target.output = newAp;
      }
    }
  }

  updateIO(type, name, target, valueType, value) {
    let ap = target.inputs.find((o) => o.name === name && o.type === type);
    if (ap) {
      ap.valueType = valueType;
      ap.value = value;
    }
  }

  /**
   * Removes an attachment point from an atom.
   * @param {boolean} type - The type of the IO (input or output).
   * @param {string} name - The name of the new attachment point.
   * @param {object} target - The attom which the attachment point is attached to. Should
   * @param {object} silent - Should any connected atoms be informed of the change
   */
  removeIO(type, name, target, silent = false) {
    //Remove the target IO attachment point
    target.inputs.forEach((input) => {
      if (input.name == name && input.type == type) {
        target.inputs.splice(target.inputs.indexOf(input), 1);
        input.deleteSelf(silent);
      }
    });
  }

  /**
   * Returns an error handler function usable with Promise.catch.
   * Prints the stack trace of a thrown error in the console and sets
   * an alert on this atom with the message of the error.
   * @returns
   */
  alertingErrorHandler() {
    return (err) => {
      this.processing = false;
      console.log(err);
      this.setAlert(err.message);
    };
  }

  /**
   * Set an alert to display next to the atom.
   * @param {string} message - The message to display.
   */
  setAlert(message) {
    this.color = "orange";
    this.alertMessage = String(message);
  }

  /**
   * Clears the alert message attached to this atom.
   */
  clearAlert() {
    this.color = this.defaultColor;
    this.alertMessage = "";
  }

  /**
   * Delineates bounds for selection box.
   */
  selectBox(x, y, xEnd, yEnd) {
    let xIn = Math.min(x, xEnd);
    let xOut = Math.max(x, xEnd);
    let yIn = Math.min(y, yEnd);
    let yOut = Math.max(y, yEnd);
    let xInPixels = GlobalVariables.widthToPixels(this.x);
    let yInPixels = GlobalVariables.heightToPixels(this.y);
    if (xInPixels >= xIn && xInPixels <= xOut) {
      if (yInPixels >= yIn && yInPixels <= yOut) {
        //this.isMoving = true
        this.selected = true;
      }
    }
  }

  /**
   * Set the atom's response to a mouse click. This usually means selecting the atom and displaying it's contents in 3D
   * @param {number} x - The X coordinate of the click
   * @param {number} y - The Y coordinate of the click
   * @param {boolean} clickProcessed - A flag to indicate if the click has already been processed
   */
  clickDown(x, y, clickProcessed) {
    let xInPixels = GlobalVariables.widthToPixels(this.x);
    let yInPixels = GlobalVariables.heightToPixels(this.y);
    let radiusInPixels = GlobalVariables.widthToPixels(this.radius);
    let atomSelected;
    //If none of the inputs processed the click see if the atom should, if not clicked, then deselected
    if (
      !clickProcessed &&
      GlobalVariables.distBetweenPoints(x, xInPixels, y, yInPixels) <
        radiusInPixels
    ) {
      this.isMoving = true;
      this.selected = true;
      atomSelected = this;
      this.sendToRender();
    }
    //Deselect this if it wasn't clicked on, unless control is held
    else if (!GlobalVariables.ctrlDown) {
      this.selected = false;
    }
    //Returns true if something was done with the click
    this.inputs.forEach((child) => {
      if (child.clickDown(x, y, clickProcessed) == true) {
        clickProcessed = true;
      }
    });
    if (this.output && !atomSelected) {
      if (this.output.clickDown(x, y, clickProcessed) == true) {
        clickProcessed = true;
      }
    }

    return atomSelected;
  }

  /**
   * Set the atom's response to a mouse double click. By default this isn't to do anything other than mark the double click as handled.
   * @param {number} x - The X cordinate of the click
   * @param {number} y - The Y cordinate of the click
   */
  doubleClick(x, y) {
    //returns true if something was done with the click
    let xInPixels = GlobalVariables.widthToPixels(this.x);
    let yInPixels = GlobalVariables.heightToPixels(this.y);
    var clickProcessed = false;

    var distFromClick = GlobalVariables.distBetweenPoints(
      x,
      xInPixels,
      y,
      yInPixels
    );

    if (distFromClick < xInPixels) {
      clickProcessed = true;
    }

    return clickProcessed;
  }

  /**
   * Set the atom's response to a mouse click up. If the atom is moving this makes it stop moving.
   * @param {number} x - The X cordinate of the click
   * @param {number} y - The Y cordinate of the click
   */
  clickUp(x, y) {
    this.isMoving = false;

    this.inputs.forEach((child) => {
      child.clickUp(x, y);
    });
    if (this.output) {
      this.output.clickUp(x, y);
    }
  }

  /**
   * Handle change in position of the mouse. If this atom is currently being moved it's position will be
   * dragged along with the mouse.
   * Also forwards the mouse move event to children of this atom so they can react if needed.
   * @param {number} x - The X cordinate of the click
   * @param {number} y - The Y cordinate of the click
   */
  mouseMove(x, y) {
    let xInPixels = GlobalVariables.widthToPixels(this.x);
    let yInPixels = GlobalVariables.heightToPixels(this.y);
    let radiusInPixels = GlobalVariables.widthToPixels(this.radius);
    if (this.isMoving == true) {
      this.x = GlobalVariables.pixelsToWidth(x);
      this.y = GlobalVariables.pixelsToHeight(y);
    }

    this.inputs.forEach((child) => {
      child.mouseMove(x, y);
    });
    if (this.output) {
      this.output.mouseMove(x, y);
    }

    var distFromClick = GlobalVariables.distBetweenPoints(
      x,
      xInPixels,
      y,
      yInPixels
    );

    //If we are close to the attachment point move it to it's hover location to make it accessible
    if (distFromClick < radiusInPixels) {
      this.showHover = true;
    } else {
      this.showHover = false;
    }
  }

  /**
   * Set the atom's response to a key press. Is used to delete the atom if it is selected.
   * @param {string} key - The key which has been pressed.
   */
  keyPress(key) {
    this.inputs.forEach((child) => {
      child.keyPress(key);
    });
  }

  /**
   * Delete this atom. Silent prevents it from telling its neighbors
   */
  deleteNode(backgroundClickAfter = true, deletePath = true, silent = false) {
    this.inputs.forEach((input) => {
      //disable the inputs before deleting
      input.ready = false;
    });

    const inputsCopy = [...this.inputs]; //Make a copy of the inputs list to delete all of them
    inputsCopy.forEach((input) => {
      input.deleteSelf(silent);
    });
    if (this.output) {
      this.output.deleteSelf(silent);
    }
    /* Remove from worker library */
    GlobalVariables.cad.deleteFromLibrary(this.uniqueID).then(() => {});

    this.parent.nodesOnTheScreen.splice(
      this.parent.nodesOnTheScreen.indexOf(this),
      1
    ); //remove this node from the list
  }

  /**
   * Runs with each frame to draw the atom.
   */
  update() {
    this.inputs.forEach((child) => {
      child.update();
    });
    if (this.output) {
      this.output.update();
    }

    this.draw();
  }

  /**
   * Create an object containing the information about this atom that we want to save.
   */
  serialize(offset = { x: 0, y: 0 }) {
    //Offsets are used to make copy and pasted atoms move over a little bit
    var ioValues = [];
    this.inputs.forEach((io) => {
      if (
        typeof io.getValue() == "number" ||
        typeof io.getValue() == "string"
      ) {
        var saveIO = {
          name: io.name,
          ioValue: io.getValue(),
        };
        ioValues.push(saveIO);
      }
    });

    var object = {
      atomType: this.atomType,
      name: this.name,
      x: this.x + offset.x,
      y: this.y - offset.y,
      uniqueID: this.uniqueID,
      ioValues: ioValues,
      description: this.description,
    };
    return object;
  }

  /**
   * Return any contribution from this atom to the README file
   */
  requestReadme() {
    //request any contributions from this atom to the readme

    return [];
  }

  /**
   * Set's the output value and shows the atom output on the 3D view.
   */
  decreaseToProcessCountByOne() {
    GlobalVariables.topLevelMolecule.census();
  }

  /**
   * Token update value function to give each atom one by default
   */
  updateValue() {
    this.waitOnComingInformation();
  }

  /**
   * Used to walk back out the tree generating a list of constants...used for evolve
   */
  walkBackForConstants(callback) {
    //Pass the call further up the chain
    this.inputs.forEach((input) => {
      input.connectors.forEach((connector) => {
        connector.walkBackForConstants(callback);
      });
    });
  }

  /**
   * Sets the atom to wait on coming information. Basically a pass through, but used for molecules
   */
  waitOnComingInformation() {
    if (this.output) {
      this.output.waitOnComingInformation();
    }
    if (this.processing) {
      //console.log("information sent to something processing");
      // this.processing = false;
    }
  }

  /**
   * Calls a worker thread to compute the atom's value.
   */
  basicThreadValueProcessing() {
    this.decreaseToProcessCountByOne();
    this.clearAlert();
    if (this.output) {
      this.output.setValue(this.uniqueID);
      this.output.ready = true;
    }
    this.processing = false;
    if (this.selected) {
      this.sendToRender();
    }
  }

  /**
   * Starts propagation placeholder. Most atom types do not begin propagation.
   */
  beginPropagation() {}

  /**
   * Returns an array of length two indicating that this is one atom and if it is waiting to be computed
   */
  census() {
    var waiting = 0;
    this.inputs.forEach((input) => {
      if (input.ready != true) {
        waiting = 1;
      }
    });
    return [1, waiting];
  }

  /**
   * Send the value of this atom to the 3D display.
   */
  sendToRender() {
    //Send code to JSxCAD to render
    try {
      GlobalVariables.writeToDisplay(this.uniqueID);
    } catch (err) {
      this.setAlert(err);
    }
  }
  /**
   * Create Leva Menu Inputs - returns to ParameterEditor
   */
  createLevaInputs() {
    let inputParams = {};

    /** Runs through active atom inputs and adds IO parameters to default param*/
    if (this.inputs) {
      this.inputs.map((input) => {
        const checkConnector = () => {
          return input.connectors.length > 0;
        };

        /* Makes inputs for Io's other than geometry */
        if (input.valueType !== "geometry") {
          inputParams[this.uniqueID + input.name] = {
            value: input.value,
            label: input.name,
            step: 0.25,
            disabled: checkConnector(),
            onChange: (value) => {
              if (input.value !== value) {
                input.setValue(value);
                //this.sendToRender();
              }
            },
          };
        }
      });
      return inputParams;
    }
  }
  /**
   * Find the value of an input for with a given name.
   * @param {string} ioName - The name of the target attachment point.
   */
  findIOValue(ioName) {
    ioName = ioName.split("~").join("");
    var ioValue = null;

    this.inputs.forEach((child) => {
      if (child.name == ioName && child.type == "input") {
        ioValue = child.getValue();
      }
    });
    return ioValue;
  }
}

import Connector from "./connector.js";
import GlobalVariables from "../js/globalvariables.js";

/**
 * This class creates a new attachmentPoint which are the input and output blobs on Atoms
 */
export default class AttachmentPoint {
  // Constant dictates how far from the parent molecule APs are rendered when in a hover position.
  // Expressed as a multiple of the parents radius.
  static get DIST_FROM_PARENT() {
    return 3.5;
  }

  // Constant dictates how much larger an AP becomes when it's activated for selection, ie, when clicking
  // or unclicking will engage the AP.
  static get TARGET_SCALEUP() {
    return 1.2;
  }

  // Constant dictates the radius of all APs, as a fraction of page width.
  static get RADIUS() {
    return 1 / 150;
  }

  /**
   * The constructor function.
   * @param {object} values An array of values passed in which will be assigned to the class as this.x
   */
  constructor(values) {
    /**
     * Whether this AP is currently visible in the Flow Canvas, eg if the mouse is close to this
     * APs parent molecule.
     */
    this.isVisible = false;

    /**
     * If this AP is in a 'targetted' state. This AP is 'targetted' if a at the mouse's current location a
     * click or release will activate this AP, starting or completing a connection respectively.
     */
    this.isTargetted = false;

    /**
     * The current position of this AP. Measured in fraction of canvas width (x) or canvas height (x).
     */
    this.x;
    this.y;

    /**
     * A unique identifying number for this attachment point among all other elements on the Flow Canvas.
     * @type {number}
     */
    this.uniqueID = 0;

    /**
     * The attachment point type.
     * @type {string}
     */
    this.atomType = "AttachmentPoint";

    /**
     * The attachment point value type. Options are number, geometry, array.
     * @type {string}
     */
    this.valueType = "number";

    /**
     * The attachment point type. Options are input, output.
     * @type {string}
     */
    this.type = "output";

    /**
     * The attachment point current value.
     * @type {number}
     */
    this.value = 10;

    /**
     * The default value to be used by the ap when nothing is attached
     * @type {string}
     */
    this.defaultValue = 10;

    /**
     * A flag to indicate if the attachment point is currently ready. Used to order initilization when program is loaded.
     * @type {string}
     */
    this.ready = true;

    /**
     * A list of all of the connectors attached to this attachment point
     * @type {object}
     */
    this.connectors = [];

    for (var key in values) {
      /**
       * Assign values in values as this.x
       */
      this[key] = values[key];
    }

    // Initially hide this attachment point.
    this.unexpand();
  }

  /**
   * Draws the attachment point on the screen. Called with each frame.
   */
  draw() {
    // No-op if this AP is not currently visible.
    if (!this.isVisible) {
      return;
    }
    let xInPixels = GlobalVariables.widthToPixels(this.x);
    let yInPixels = GlobalVariables.heightToPixels(this.y);
    let radiusInPixels = GlobalVariables.widthToPixels(AttachmentPoint.RADIUS);

    if (this.isTargetted) {
      radiusInPixels = radiusInPixels * AttachmentPoint.TARGET_SCALEUP;
    }

    GlobalVariables.c.font = "10px Work Sans";
    var textWidth = GlobalVariables.c.measureText(this.name).width;

    var bubbleColor =
      this.name === "geometry" ? this.parentMolecule.selectedColor : "#C300FF";
    var halfRadius = radiusInPixels * 0.5;
    GlobalVariables.c.globalCompositeOperation = "source-over";
    GlobalVariables.c.beginPath();
    GlobalVariables.c.fillStyle = bubbleColor;

    var topEdge = yInPixels - radiusInPixels;
    var leftEdge = xInPixels;
    if (this.type == "input") {
      leftEdge = xInPixels - textWidth - radiusInPixels - halfRadius;
    }

    var textStart = leftEdge;
    if (this.type == "output") {
      textStart = leftEdge + radiusInPixels + halfRadius;
    }

    // Draw pill-shape for the text of this AP
    GlobalVariables.c.arc(
      leftEdge,
      yInPixels,
      radiusInPixels,
      Math.PI / 2,
      (-1 * Math.PI) / 2
    );
    GlobalVariables.c.rect(
      leftEdge,
      topEdge,
      textWidth + radiusInPixels + halfRadius,
      radiusInPixels * 2
    );
    GlobalVariables.c.arc(
      leftEdge + textWidth + radiusInPixels + halfRadius,
      yInPixels,
      radiusInPixels,
      (-1 * Math.PI) / 2,
      Math.PI / 2
    );
    GlobalVariables.c.fill();

    // Draw text name of this AP
    GlobalVariables.c.beginPath();
    GlobalVariables.c.fillStyle = this.parentMolecule.defaultColor;
    GlobalVariables.c.fillText(this.name, textStart, yInPixels + 2);
    GlobalVariables.c.fill();
    GlobalVariables.c.closePath();

    // Draw the circular connection target
    GlobalVariables.c.beginPath();
    if (this.ready) {
      GlobalVariables.c.fillStyle = this.parentMolecule.color;
    } else {
      GlobalVariables.c.fillStyle = "#6ba4ff";
    }
    GlobalVariables.c.strokeStyle = this.parentMolecule.strokeColor;
    GlobalVariables.c.lineWidth = 1;

    GlobalVariables.c.arc(
      xInPixels,
      yInPixels,
      radiusInPixels,
      0,
      Math.PI * 2,
      false
    );
    GlobalVariables.c.fill();
    GlobalVariables.c.stroke();
    GlobalVariables.c.closePath();
  }

  /**
   * Handles mouse click down. If the click is inside the AP it's connectors are selected if it is an input.
   * @param {number} x - The x coordinate of the click
   * @param {number} y - The y coordinate of the click
   * @param {boolean} clickProcessed - Has the click already been handled
   */
  clickDown(x, y, clickProcessed) {
    if (this.isCloseEnoughToTarget(x, y) && !clickProcessed) {
      if (this.type == "output") {
        //begin to extend a connector from this if it is an output
        new Connector({
          parentMolecule: this.parentMolecule,
          attachmentPoint1: this,
          atomType: "Connector",
          isMoving: true,
        });
      }
      if (this.type == "input") {
        //connectors can only be selected by clicking on an input
        this.connectors.forEach((connector) => {
          //select any connectors attached to this node
          connector.selected = true;
        });
      }

      return true; //indicate that the click was handled by this object
    } else {
      if (this.type == "input") {
        //connectors can only be selected by clicking on an input
        this.connectors.forEach((connector) => {
          //unselect any connectors attached to this node
          connector.selected = false;
        });
      }
      return false; //indicate that the click was not handled by this object
    }
  }

  /**
   * Handles mouse click up. If the click is inside the AP and a connector is currently extending, then a connection is made
   * @param {number} x - The x coordinate of the click
   * @param {number} y - The y coordinate of the click
   */
  clickUp(x, y) {
    this.connectors.forEach((connector) => {
      connector.clickUp(x, y);
    });
  }

  /**
   * Handles mouse click and move to expand the AP.
   * @param {number} x - The x coordinate of the click
   * @param {number} y - The y coordinate of the click
   */
  mouseMove(x, y) {
    let activationBoundary =
      AttachmentPoint.DIST_FROM_PARENT * this.parentMolecule.radius;

    let parentXInPixels = GlobalVariables.widthToPixels(this.parentMolecule.x);
    let parentYInPixels = GlobalVariables.heightToPixels(this.parentMolecule.y);
    if (
      GlobalVariables.distBetweenPoints(
        parentXInPixels,
        x,
        parentYInPixels,
        y
      ) <= GlobalVariables.widthToPixels(activationBoundary)
    ) {
      this.isVisible = true;
      [this.x, this.y] = this.computePosition(activationBoundary);
      [this.x, this.y] = GlobalVariables.constrainToCanvasBorders(
        this.x,
        this.y
      );
      this.isTargetted = this.isCloseEnoughToTarget(x, y);
    } else {
      this.unexpand();
    }

    this.connectors.forEach((connector) => {
      connector.mouseMove(x, y);
    });
  }

  /**
   * Unexpands this attachment point, eg: when the app starts, when the mouse
   * is moved out of the expansion range, etc.
   */
  unexpand() {
    this.isVisible = false;
    this.isTargetted = false;
    // Also restore this.x and this.x to be on the perimiter of parent module
    // since those values are used when rendering connectors.
    this.y = this.parentMolecule.y;
    if (this.type == "input") {
      this.x = this.parentMolecule.x - this.parentMolecule.radius;
    } else {
      this.x = this.parentMolecule.x + this.parentMolecule.radius;
    }
    [this.x, this.y] = GlobalVariables.constrainToCanvasBorders(this.x, this.y);
  }

  /**
   * Computes the correct position for this AP based on parent and the provided boundary.
   * Returns a tuple of [xposition, yposition] both values in fraction-of-screen units.
   * @param {} boundary - radius of the boundary within which APs must be displayed relative to
   * the parent molecule.
   */
  computePosition(boundary) {
    const inputList = this.parentMolecule.inputs.filter(
      (input) => input.type == "input"
    );
    if (this.type == "output") {
      // Outputs are always singular and always positioned partially overlapped by the right-most
      // pole of the parent molecule.
      return [
        this.parentMolecule.x +
          this.parentMolecule.radius +
          AttachmentPoint.RADIUS * 0.75,
        this.parentMolecule.y,
      ];
    } else if (this.type == "input" && inputList.length == 1) {
      // Singular inputs are located in a mirror of the output, ie partially overlapped by the
      // left-most pole of the parent molecule.
      return [
        this.parentMolecule.x -
          this.parentMolecule.radius -
          AttachmentPoint.RADIUS * 0.75,
        this.parentMolecule.y,
      ];
    } else {
      // This is one of several input APs for the parent molecule.
      // Otherwise APs are spaced in an arc at a distance around the parent molecule.
      const attachmentPointNumber = inputList.indexOf(this);
      const anglePerIO = Math.PI / (inputList.length + 1);
      // Reduce radius to ensure that the entire attachment point is inside boundary, even when targetted.
      const hoverRadius =
        boundary - AttachmentPoint.RADIUS * AttachmentPoint.TARGET_SCALEUP;

      // angle correction so that it centers menu adjusting to however many attachment points there are
      const angleCorrection = Math.PI / 2 + anglePerIO;
      let hoverOffsetX =
        hoverRadius *
        Math.cos(attachmentPointNumber * anglePerIO + angleCorrection);

      // Do this calculation in pixels. The fractional units of height(y) might not be 1:1 proportionate with
      // fractional units of width(x) if the canvas is rectangular. We always want these APs to look like they're
      // in a circular pattern so do this calculation in pixels then convert back to height fraction.
      let hoverOffsetY =
        -1 *
        GlobalVariables.pixelsToHeight(
          GlobalVariables.widthToPixels(hoverRadius) *
            Math.sin(attachmentPointNumber * anglePerIO + angleCorrection)
        );

      return [
        this.parentMolecule.x + hoverOffsetX,
        this.parentMolecule.y + hoverOffsetY,
      ];
    }
  }

  /**
   * Returns true if the given point is close enough to this AP that this AP should be "targetted",
   * ie, should treat clicks or mouse-releases as if they hit this AP.
   * Always false if this AP isn't visible.
   *
   * @param {} x - position in pixels
   * @param {*} y - position in pixels
   */
  isCloseEnoughToTarget(x, y) {
    if (!this.isVisible) {
      return false;
    }
    const dist = GlobalVariables.distBetweenPoints(
      x,
      GlobalVariables.widthToPixels(this.x),
      y,
      GlobalVariables.heightToPixels(this.y)
    );
    const apRadiusInPixels = GlobalVariables.widthToPixels(
      AttachmentPoint.RADIUS
    );
    if (this.type == "output") {
      return dist <= apRadiusInPixels * 2;
    } else {
      // this.type == "input"
      let targetRadius = apRadiusInPixels * 2;
      // check if this creates overlapping target areas in the case where there's multiple inputs.
      // If so reduce the targetting radius.
      const inputCount = this.parentMolecule.inputs.filter(
        (input) => input.type == "input"
      ).length;

      let hoverRadius = GlobalVariables.widthToPixels(
        AttachmentPoint.DIST_FROM_PARENT * this.parentMolecule.radius -
          AttachmentPoint.RADIUS * AttachmentPoint.TARGET_SCALEUP
      );

      const anglePerIO = Math.PI / (inputCount + 1);
      const maxNonOverlappingRadius = hoverRadius * Math.sin(anglePerIO / 2);

      targetRadius = Math.max(
        apRadiusInPixels,
        Math.min(targetRadius, maxNonOverlappingRadius)
      );
      return dist < targetRadius;
    }
  }

  /**
   * Just passes a key press to the attached connectors. No impact on the connector.
   * @param {string} key - The key which was pressed
   */
  keyPress(key) {
    this.connectors.forEach((connector) => {
      connector.keyPress(key);
    });
  }

  /**
   * Delete any connectors attached to this ap
   */
  deleteSelf(silent = false) {
    //remove any connectors which were attached to this attachment point
    var connectorsList = [...this.connectors]; //Make a copy of the list so that we can delete elements without having issues with forEach as we remove things from the list
    connectorsList.forEach((connector) => {
      connector.deleteSelf(silent);
    });
  }

  /**
   * Delete a target connector which is passed in. The default option is to delete all of the connectors.
   */
  deleteConnector(connector = "all") {
    try {
      const connectorIndex = this.connectors.indexOf(connector);
      if (connectorIndex != -1) {
        this.connectors.splice(connectorIndex, 1); //Remove the target connector
      } else {
        this.connectors = []; //Remove all of the connectors
      }
    } catch (err) {
      console.warn("Error deleting connector: ");
      console.warn(err);
    }
  }

  /**
   * Can be called to see if the target coordinates are within this ap. Returns true/false.
   * @param {number} x - The x coordinate of the target
   * @param {number} y - The y coordinate of the target
   */
  wasConnectionMade(x, y) {
    return this.isCloseEnoughToTarget(x, y) && this.connectors.length == 0;
  }

  /**
   * Attaches a new connector to this ap
   * @param {object} connector - The connector to attach
   */
  attach(connector) {
    this.connectors.push(connector);
  }

  /**
   * Starts propagation from this attachmentPoint if it is not waiting for anything up stream.
   */
  beginPropagation() {
    //If nothing is connected it is a starting point
    if (this.connectors.length == 0) {
      this.setValue(this.value);
    }
  }

  /**
   * Passes a lock command to the parent molecule, or to the attached connector depending on input/output.
   */
  waitOnComingInformation() {
    if (this.type == "output") {
      this.connectors.forEach((connector) => {
        connector.waitOnComingInformation();
      });
    } else {
      //If this is an input
      this.ready = false;
      this.parentMolecule.waitOnComingInformation(this.name);
    }
  }

  /**
   * Restores the ap to it's default value.
   */
  setDefault() {
    this.setValue(this.defaultValue);
  }

  /**
   * Updates the default value for the ap.
   */
  updateDefault(newDefault) {
    var oldDefault = this.defaultValue;
    this.defaultValue = newDefault;

    if (this.connectors.length == 0 && this.value == oldDefault) {
      //Update the value to be the default if there is nothing attached
      this.value = this.defaultValue;
    }
  }

  /**
   * Reads and returns the current value of the ap.
   */
  getValue() {
    return this.value;
  }

  /**
   * Sets the current value of the ap. Force forces an update even if the value hasn't changed.
   */
  setValue(newValue) {
    this.value = newValue;

    this.ready = true;
    //propagate the change to linked elements if this is an output
    if (this.type == "output") {
      this.connectors.forEach((connector) => {
        //select any connectors attached to this node
        connector.propogate();
      });
    }
    //if this is an input attachment point
    else {
      this.parentMolecule.updateValue(this.name);
    }
  }

  /**
   * Computes the curent position and then draws the ap on the screen.
   */
  update() {
    this.draw();

    this.connectors.forEach((connector) => {
      //update any connectors attached to this node
      connector.update();
    });
  }
}

import GlobalVariables from "../js/globalvariables.js";

/**
 * The connector class defines how an output can be connected to an input. It appears on the screen as a black line extending from an output to an input.
 */
export default class Connector {
  /**
   * The constructor function.
   * @param {object} values An array of values passed in which will be assigned to the class as this.x
   */
  constructor(values) {
    /**
     * True if the connector is currently being created and is in the process of extending
     * @type {boolean}
     */
    this.isMoving = false;
    /**
     * The connectors current color
     * @type {string}
     */
    this.color = "black";
    /**
     * The type of this connector
     * @type {string}
     */
    this.atomType = "Connector";
    /**
     * True if this connector has been selected
     * @type {boolean}
     */
    this.selected = false;
    /**
     * The first attachment point this connector is connected to (an ouput)
     * @type {object}
     */
    this.attachmentPoint1 = null;
    /**
     * The second attachment point this connector is connected to (an input)
     * @type {object}
     */
    this.attachmentPoint2 = null;

    for (var key in values) {
      /**
       * Assign each of the values in values as this.key
       */
      this[key] = values[key];
    }

    /**
     * The starting X cordinate for the connector. Should really be referenced to attachmentPoint1.
     * @type {number}
     */
    this.startX = this.attachmentPoint1.parentMolecule.outputX;
    /**
     * The starting Y cordinate for the connector. Should really be referenced to attachmentPoint1.
     * @type {number}
     */
    this.startY = this.attachmentPoint1.parentMolecule.y;

    this.attachmentPoint1.connectors.push(this); //Give input and output references to the connector
    if (this.attachmentPoint2 != null) {
      this.attachmentPoint2.connectors.push(this);
    }
  }

  /**
   * Draw the connector as a bezier curve on the screen
   */
  draw() {
    let startXInPixels = GlobalVariables.widthToPixels(this.startX);
    let startYInPixels = GlobalVariables.heightToPixels(this.startY);
    let endXInPixels = GlobalVariables.widthToPixels(this.endX);
    let endYInPixels = GlobalVariables.heightToPixels(this.endY);

    GlobalVariables.c.beginPath();
    GlobalVariables.c.fillStyle = this.color;
    GlobalVariables.c.strokeStyle = this.color;
    GlobalVariables.c.globalCompositeOperation = "destination-over"; //draw under other elements;
    if (this.selected) {
      GlobalVariables.c.lineWidth = 3;
    } else {
      GlobalVariables.c.lineWidth = 1;
    }
    GlobalVariables.c.moveTo(startXInPixels, startYInPixels);
    GlobalVariables.c.bezierCurveTo(
      startXInPixels + 100,
      startYInPixels,
      endXInPixels - 100,
      endYInPixels,
      endXInPixels,
      endYInPixels
    );
    GlobalVariables.c.stroke();
    GlobalVariables.c.globalCompositeOperation = "source-over"; //switch back to drawing on top
  }

  /**
   * clickUp checks to see if the mouse button has been released over an input attachment point. If it has then the connector is created there. If not, then the connector is deleted.
   * @param {number} x - The x cordinate of the click
   * @param {number} y - The y cordinate of the click
   */
  clickUp(x, y) {
    if (this.isMoving) {
      //we only want to attach the connector which is currently moving
      var attachmentMade = false;
      GlobalVariables.currentMolecule.nodesOnTheScreen.forEach((molecule) => {
        //For every molecule on the screen
        molecule.inputs.forEach((attachmentPoint) => {
          //For each of their attachment points
          if (attachmentPoint.wasConnectionMade(x, y) && !attachmentMade) {
            /** Prevent it from connecting to itself  */
            if (
              this.attachmentPoint1.parentMolecule !==
              attachmentPoint.parentMolecule
            ) {
              //Check to make sure we haven't already attached somewhere else
              attachmentMade = true;
              this.attachmentPoint2 = attachmentPoint;
              attachmentPoint.attach(this);
              this.propogate();
            }
          }
        });
      });
      if (!attachmentMade) {
        this.deleteSelf();
      }
      this.isMoving = false;
    }
  }

  /**
   * Handle movements of the mouse while connector is being created. As long as the mouse is pressed down,
   * the end of the connector stays attached to the mouse.
   * @param {number} x - The x cordinate of the click
   * @param {number} y - The y cordinate of the click
   */
  mouseMove(x, y) {
    if (this.isMoving == true) {
      /**
       * The s cordinate of the end of the connector.
       */
      this.endX = GlobalVariables.pixelsToWidth(x);
      /**
       * The y cordinate of the end of the connector.
       */
      this.endY = GlobalVariables.pixelsToHeight(y);
    }
  }

  /**
   * Called when any key is pressed. If the key is delete or backspace and the connector is selected then the connector is deleted.
   * @param {string} key - The key which was pressed
   */
  keyPress(key) {
    if (this.selected) {
      if (["Delete", "Backspace"].includes(key)) {
        this.deleteSelf();
      }
    }
  }

  /**
   * Deletes the connector by calling its attachmentPoints to tell them to delete their references to this connector.
   */
  deleteSelf(silent = false) {
    //Remove this connector from the output it is attached to
    this.attachmentPoint1.deleteConnector(this);

    //Free up the input to which this was attached
    if (this.attachmentPoint2 != null) {
      this.attachmentPoint2.deleteConnector(this);
      if (!silent) {
        this.attachmentPoint2.setDefault();
      }
    }
  }

  /**
   * Generates an object used to save the connector.
   */
  serialize() {
    if (this.attachmentPoint2 != null) {
      var object = {
        ap1Name: this.attachmentPoint1.name,
        ap2Name: this.attachmentPoint2.name,
        ap2Primary: this.attachmentPoint2.primary,
        ap1ID: this.attachmentPoint1.parentMolecule.uniqueID,
        ap2ID: this.attachmentPoint2.parentMolecule.uniqueID,
      };
      return object;
    }
  }

  /**
   * Passes a lock call to the connected input.
   */
  waitOnComingInformation() {
    if (this.attachmentPoint2) {
      this.attachmentPoint2.waitOnComingInformation();
    }
  }

  /**
   * Pass the value of the attached output to the attached input
   */
  propogate() {
    //takes the input and passes it to the output
    if (this.attachmentPoint1.ready && this.attachmentPoint2) {
      this.attachmentPoint2.setValue(this.attachmentPoint1.getValue());
    }
  }

  /**
   * Used to walk back out the tree generating a list of constants...used for evolve
   */
  walkBackForConstants(callback) {
    this.attachmentPoint1.parentMolecule.walkBackForConstants(callback);
  }

  /**
   * Computes the connectors position and draw it to the screen.
   */
  update() {
    this.startX = this.attachmentPoint1.x;
    this.startY = this.attachmentPoint1.y;
    if (this.attachmentPoint2) {
      //check to see if the attachment point is defined
      this.endX = this.attachmentPoint2.x;
      this.endY = this.attachmentPoint2.y;
    }
    this.draw();
  }
}

